import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search, ShoppingCart, Star, PlayCircle, ChevronDown, Menu, X, BookOpen, Users, BarChart2, Briefcase, LifeBuoy, Globe, Edit3, MessageSquare, Loader2, AlertTriangle, Sparkles } from 'lucide-react';

// Helper to format price
const formatPrice = (price) => {
  return new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(price);
};

// Star Rating Component
const StarRating = ({ rating, reviews }) => {
  const fullStars = Math.floor(rating);
  const halfStar = rating % 1 >= 0.5;
  const emptyStars = 5 - fullStars - (halfStar ? 1 : 0);

  return (
    <div className="flex items-center">
      <span className="text-sm font-bold text-orange-500 mr-1">{rating.toFixed(1)}</span>
      {[...Array(fullStars)].map((_, i) => (
        <Star key={`full-${i}`} className="h-4 w-4 text-yellow-400 fill-current" />
      ))}
      {halfStar && <Star key="half" className="h-4 w-4 text-yellow-400 fill-current" style={{ clipPath: 'inset(0 50% 0 0)' }} />}
      {[...Array(emptyStars)].map((_, i) => (
        <Star key={`empty-${i}`} className="h-4 w-4 text-gray-300 fill-current" />
      ))}
      {reviews > 0 && <span className="text-xs text-gray-500 ml-1">({reviews.toLocaleString()})</span>}
    </div>
  );
};

// Course Card Component
const CourseCard = ({ course }) => {
  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-xl transition-shadow duration-300 w-full">
      <img src={course.imageUrl} alt={course.title} className="w-full h-40 object-cover" />
      <div className="p-4">
        <h3 className="text-md font-semibold text-gray-800 mb-1 h-12 overflow-hidden">{course.title}</h3>
        <p className="text-xs text-gray-500 mb-2">{course.instructor}</p>
        <StarRating rating={course.rating} reviews={course.reviews} />
        <div className="flex items-baseline my-2">
          <p className="text-lg font-bold text-gray-900 mr-2">{formatPrice(course.price)}</p>
          {course.originalPrice && (
            <p className="text-sm text-gray-500 line-through">{formatPrice(course.originalPrice)}</p>
          )}
        </div>
        {course.bestseller && (
          <span className="text-xs font-semibold bg-yellow-200 text-yellow-800 px-2 py-1 rounded">Bestseller</span>
        )}
      </div>
    </div>
  );
};

// Navigation Component
const Navbar = ({ setCurrentPage }) => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isCategoriesMenuOpen, setIsCategoriesMenuOpen] = useState(false);
  const categories = [
    'Development', 'Business', 'Finance & Accounting', 'IT & Software', 
    'Office Productivity', 'Personal Development', 'Design', 'Marketing', 
    'Lifestyle', 'Photography & Video', 'Health & Fitness', 'Music', 'Teaching & Academics'
  ];

  return (
    <nav className="bg-white shadow-md sticky top-0 z-50">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          <div className="flex items-center">
            <button onClick={() => setCurrentPage('home')} className="flex-shrink-0 flex items-center">
              <BookOpen className="h-8 w-8 text-purple-600" />
              <span className="ml-2 text-xl font-bold text-gray-800 hidden md:inline">CoursePlatform</span>
            </button>
            <div className="hidden md:block ml-6 relative">
              <button 
                onMouseEnter={() => setIsCategoriesMenuOpen(true)}
                onMouseLeave={() => setIsCategoriesMenuOpen(false)}
                className="text-gray-600 hover:text-purple-600 px-3 py-2 rounded-md text-sm font-medium flex items-center"
              >
                Categories <ChevronDown className="h-4 w-4 ml-1" />
              </button>
              {isCategoriesMenuOpen && (
                 <div 
                  onMouseEnter={() => setIsCategoriesMenuOpen(true)}
                  onMouseLeave={() => setIsCategoriesMenuOpen(false)}
                  className="absolute left-0 mt-2 w-64 bg-white rounded-md shadow-lg py-1 z-20"
                 >
                  {categories.slice(0,10).map(category => (
                    <a key={category} href="#" onClick={(e) => {e.preventDefault(); setCurrentPage('home'); /* TODO: Filter by category */}} className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-purple-600">{category}</a>
                  ))}
                 </div>
              )}
            </div>
          </div>

          <div className="hidden md:flex flex-grow max-w-xl mx-4">
            <div className="relative w-full">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Search className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type="text"
                placeholder="Search for anything"
                className="block w-full bg-gray-100 border border-gray-300 rounded-full py-2 pl-10 pr-3 text-sm placeholder-gray-500 focus:outline-none focus:ring-purple-500 focus:border-purple-500 focus:bg-white"
              />
            </div>
          </div>

          <div className="hidden md:flex items-center space-x-4">
            <a href="#" onClick={(e) => {e.preventDefault(); setCurrentPage('teach');}} className="text-gray-600 hover:text-purple-600 px-3 py-2 rounded-md text-sm font-medium">Teach on Platform</a>
            <button onClick={() => setCurrentPage('cart')} className="text-gray-600 hover:text-purple-600">
              <ShoppingCart className="h-6 w-6" />
            </button>
            <button onClick={() => setCurrentPage('login')} className="text-gray-800 bg-white border border-gray-800 hover:bg-gray-100 px-4 py-2 rounded-md text-sm font-semibold">Log In</button>
            <button onClick={() => setCurrentPage('signup')} className="bg-purple-600 text-white hover:bg-purple-700 px-4 py-2 rounded-md text-sm font-semibold">Sign Up</button>
          </div>

          <div className="md:hidden flex items-center">
            <button onClick={() => setCurrentPage('cart')} className="text-gray-600 hover:text-purple-600 mr-2">
              <ShoppingCart className="h-6 w-6" />
            </button>
            <button onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)} className="text-gray-600 hover:text-purple-600">
              {isMobileMenuOpen ? <X className="h-6 w-6" /> : <Menu className="h-6 w-6" />}
            </button>
          </div>
        </div>
      </div>

      {isMobileMenuOpen && (
        <div className="md:hidden bg-white shadow-lg pb-4">
          <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
            <div className="relative mb-2 px-2">
              <div className="absolute inset-y-0 left-0 pl-5 flex items-center pointer-events-none">
                <Search className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type="text"
                placeholder="Search for anything"
                className="block w-full bg-gray-100 border border-gray-300 rounded-full py-2 pl-10 pr-3 text-sm placeholder-gray-500 focus:outline-none focus:ring-purple-500 focus:border-purple-500 focus:bg-white"
              />
            </div>
            <a href="#" onClick={(e) => {e.preventDefault(); setCurrentPage('home'); setIsMobileMenuOpen(false);}} className="block px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-purple-600 hover:bg-gray-50">Categories</a>
            <a href="#" onClick={(e) => {e.preventDefault(); setCurrentPage('teach'); setIsMobileMenuOpen(false);}} className="block px-3 py-2 rounded-md text-base font-medium text-gray-700 hover:text-purple-600 hover:bg-gray-50">Teach on Platform</a>
          </div>
          <div className="pt-4 pb-3 border-t border-gray-200">
            <div className="flex flex-col px-2 space-y-2">
              <button onClick={() => {setCurrentPage('login'); setIsMobileMenuOpen(false);}} className="w-full text-left text-gray-800 bg-white border border-gray-800 hover:bg-gray-100 px-4 py-2 rounded-md text-sm font-semibold">Log In</button>
              <button onClick={() => {setCurrentPage('signup'); setIsMobileMenuOpen(false);}} className="w-full text-left bg-purple-600 text-white hover:bg-purple-700 px-4 py-2 rounded-md text-sm font-semibold">Sign Up</button>
            </div>
          </div>
        </div>
      )}
    </nav>
  );
};

// Hero Section Component
const HeroSection = () => {
  return (
    <div className="bg-gray-100">
      <div className="container mx-auto px-4 py-12 md:py-20">
        <div className="relative bg-white shadow-lg rounded-lg p-8 md:p-12 max-w-3xl mx-auto md:mx-0">
          <h1 className="text-3xl md:text-4xl font-bold text-gray-800 mb-4">Unlock Your Potential</h1>
          <p className="text-gray-600 text-lg mb-6">Learn something new today. With thousands of courses, find the one that's right for you.</p>
          <div className="relative">
            <input 
              type="text" 
              placeholder="What do you want to learn?" 
              className="w-full py-3 px-4 rounded-md border border-gray-300 focus:ring-purple-500 focus:border-purple-500 text-lg"
            />
            <button className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-purple-600 text-white p-2 rounded-md hover:bg-purple-700">
              <Search className="h-6 w-6" />
            </button>
          </div>
           <img 
            src="https://placehold.co/400x300/A7F3D0/333?text=Learning+Illustration" 
            alt="Learning illustration" 
            className="absolute hidden lg:block right-[-250px] top-[-50px] w-[280px] h-auto rounded-lg"
            style={{transform: 'translateY(-10%) translateX(10%)'}}
          />
        </div>
      </div>
    </div>
  );
};

// Course List Section Component
const CourseListSection = ({ title, courses, setCurrentPage, isLoading }) => {
  if (isLoading) {
    return (
        <div className="container mx-auto px-4 py-8 text-center">
            <Loader2 className="h-8 w-8 animate-spin inline-block text-purple-600" />
            <p className="text-gray-600 mt-2">Loading courses...</p>
        </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-2xl font-bold text-gray-800 mb-6">{title}</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {courses.map(course => (
          <button key={course.id} onClick={() => setCurrentPage('courseDetail', course)} className="text-left">
            <CourseCard course={course} />
          </button>
        ))}
      </div>
    </div>
  );
};

// Footer Component
const Footer = () => {
  const footerLinks = [
    { title: 'Platform Business', links: ['Teach on Platform', 'Get the app', 'About us', 'Contact us'] },
    { title: 'Careers', links: ['Blog', 'Help and Support', 'Affiliate', 'Investors'] },
    { title: 'Terms', links: ['Privacy policy', 'Cookie settings', 'Sitemap', 'Accessibility statement'] },
  ];

  return (
    <footer className="bg-gray-800 text-gray-300 pt-12 pb-8">
      <div className="container mx-auto px-4">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 mb-8">
          {footerLinks.map((section, index) => (
            <div key={index}>
              <h5 className="font-semibold text-white mb-3">{section.title}</h5>
              <ul>
                {section.links.map((link, linkIndex) => (
                  <li key={linkIndex} className="mb-2">
                    <a href="#" className="hover:text-white hover:underline text-sm">{link}</a>
                  </li>
                ))}
              </ul>
            </div>
          ))}
          <div className="md:col-span-2 lg:col-span-1 lg:justify-self-end">
             <button className="border border-gray-300 hover:border-white text-white px-4 py-2 rounded-md flex items-center text-sm">
                <Globe className="h-5 w-5 mr-2" /> English
             </button>
          </div>
        </div>
        <div className="flex flex-col md:flex-row justify-between items-center border-t border-gray-700 pt-8">
          <div className="flex items-center mb-4 md:mb-0">
            <BookOpen className="h-8 w-8 text-purple-400" />
            <span className="ml-2 text-xl font-bold text-white">CoursePlatform</span>
          </div>
          <p className="text-sm">&copy; {new Date().getFullYear()} CoursePlatform, Inc.</p>
        </div>
      </div>
    </footer>
  );
};

// --- Gemini API Helper ---
const callGeminiAPI = async (promptText) => {
  // IMPORTANT: This will not work without a valid API key.
  // Get a key from Google AI Studio: https://aistudio.google.com/
  const apiKey = ""; // <-- PASTE YOUR GOOGLE AI API KEY HERE
  if (!apiKey) {
      throw new Error("Gemini API key is missing. Please add it to the callGeminiAPI function in App.js.");
  }
  const apiUrl = `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=${apiKey}`;

  const payload = {
    contents: [{ role: "user", parts: [{ text: promptText }] }],
  };

  try {
    const response = await fetch(apiUrl, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload),
    });

    if (!response.ok) {
      const errorBody = await response.json();
      console.error("Gemini API Error:", errorBody);
      throw new Error(`API request failed with status ${response.status}: ${errorBody.error?.message || 'Unknown error'}`);
    }

    const result = await response.json();

    if (result.candidates && result.candidates.length > 0 &&
        result.candidates[0].content && result.candidates[0].content.parts &&
        result.candidates[0].content.parts.length > 0) {
      return result.candidates[0].content.parts[0].text;
    } else {
      console.error("Unexpected API response structure:", result);
      throw new Error("Failed to extract text from API response.");
    }
  } catch (error) {
    console.error("Error calling Gemini API:", error);
    throw error; // Re-throw to be caught by the caller
  }
};


// --- Teach on Platform Page (with AI Description Generator) ---
const TeachPage = () => {
  const [courseTitle, setCourseTitle] = useState('');
  const [courseKeywords, setCourseKeywords] = useState('');
  const [generatedDescription, setGeneratedDescription] = useState('');
  const [isGenerating, setIsGenerating] = useState(false);
  const [error, setError] = useState('');

  const handleGenerateDescription = async () => {
    if (!courseTitle.trim()) {
      setError('Please enter a course title.');
      return;
    }
    setIsGenerating(true);
    setError('');
    setGeneratedDescription('');

    const prompt = `Generate a compelling and concise course description (around 100-150 words) for an online course titled "${courseTitle}". 
    The course focuses on the following keywords/topics: "${courseKeywords || 'general topics related to the title'}". 
    Highlight key benefits for students and what they will learn. Make it engaging.`;

    try {
      const description = await callGeminiAPI(prompt);
      setGeneratedDescription(description);
    } catch (err) {
      setError(err.message || 'Failed to generate description. Please try again.');
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Teach on CoursePlatform</h1>
      <div className="bg-white p-8 rounded-lg shadow-xl max-w-2xl mx-auto">
