// Main App Component
export default function App() {
  const [currentPage, setCurrentPage] = useState('home'); // 'home', 'courseDetail', 'login', 'signup', 'cart', 'teach'
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [courses, setCourses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchCourses = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get('http://localhost:5001/api/v1/courses');
        // Transform backend data to match frontend component props
        const transformedCourses = response.data.data.courses.map(course => ({
          id: course.Course_ID,
          title: course.Title,
          // NOTE: The following fields are placeholders. You'll need to update your API to provide this data.
          instructor: 'Dr. Angela Yu', // Placeholder - You need to JOIN with the Person table
          rating: (Math.random() * (5 - 4.2) + 4.2), // Placeholder - Random rating between 4.2 and 5.0
          reviews: Math.floor(Math.random() * 200000), // Placeholder
          price: course.Price,
          originalPrice: course.Price > 0 ? course.Price * 1.5 : 99.99, // Placeholder
          imageUrl: `https://placehold.co/300x170/E2E8F0/4A5568?text=${encodeURIComponent(course.Title.substring(0, 15))}`,
          bestseller: Math.random() > 0.5, // Placeholder
          shortDescription: course.Description,
          // Placeholder reviews. You would fetch these from your backend.
          reviewsList: [
              { id: 1, user: 'Student A', rating: 5, comment: 'Absolutely fantastic course! Covered everything I needed and more.' },
              { id: 2, user: 'Student B', rating: 4, comment: 'Very comprehensive. A bit fast-paced at times, but the content is top-notch.' },
          ]
        }));
        setCourses(transformedCourses);
      } catch (error) {
        console.error('Error fetching courses:', error);
      } finally {
        setIsLoading(false);
      }
    };
    fetchCourses();
  }, []);

  const navigateToPage = (page, data = null) => {
    setSelectedCourse(data);
    setCurrentPage(page);
    window.scrollTo(0, 0); 
  };

  let content;
  switch (currentPage) {
    case 'home':
      content = <HomePage courses={courses} isLoading={isLoading} setCurrentPage={navigateToPage} />;
      break;
    case 'courseDetail':
      content = <CourseDetailPage course={selectedCourse} setCurrentPage={navigateToPage} />;
      break;
    case 'login':
      content = <LoginPage setCurrentPage={navigateToPage} />;
      break;
    case 'signup':
      content = <SignupPage setCurrentPage={navigateToPage} />;
      break;
    case 'cart':
      content = <CartPage courses={courses} setCurrentPage={navigateToPage} />;
      break;
    case 'teach':
      content = <TeachPage />;
      break;
    default:
      content = <HomePage courses={courses} isLoading={isLoading} setCurrentPage={navigateToPage} />;
  }

  const showLayout = currentPage !== 'login' && currentPage !== 'signup';

  return (
    <div className="font-sans antialiased text-gray-900 bg-gray-50">
      {showLayout && <Navbar setCurrentPage={navigateToPage} />}
      <main>
        {content}
      </main>
      {showLayout && <Footer />}
    </div>
  );
}
