import React from 'react';
import HeroSection from '../components/HeroSection';
import CourseListSection from '../components/CourseListSection';

const HomePage = ({ courses, isLoading, setCurrentPage }) => {
  const popularCourses = courses.filter(c => c.bestseller).slice(0, 8);
  const latestCourses = courses.slice(0, 8);

  return (
    <div className="bg-white">
      <HeroSection />
      {/* The ID is used by the HeroSection button to scroll to this point */}
      <div id="courses-section" className="bg-gray-50">
        <CourseListSection 
          title="Our Bestsellers" 
          courses={popularCourses.length > 0 ? popularCourses : latestCourses} 
          setCurrentPage={setCurrentPage}
          isLoading={isLoading}
        />
      </div>
    </div>
  );
};

export default HomePage;
