import React from 'react';
// Removed Search import as the search bar is being removed

// Hero Section Component
const HeroSection = () => {
  return (
    <div
      className="relative w-full min-h-screen bg-cover bg-center" // Added min-h-screen for height, bg-cover, bg-center for image properties
      style={{ backgroundImage: 'url(/Homepage_logo.png)', backgroundAttachment: 'fixed' }} // Set background image and fixed attachment
      role="img" // Indicate that this div serves as an image
      aria-label="Homepage hero background" // Provide accessible description for the background image
    >
      {/* Content for the hero section would go here if needed to scroll over the fixed background.
          Currently, it's just the background image as per the request.
      */}
    </div>
  );
};

export default HeroSection;
