import React, { useState } from 'react';
import CourseListSection from '../components/CourseListSection';

const COURSES_PER_PAGE = 20;

const AllCoursesPage = ({ courses, isLoading }) => {
  const [visibleCoursesCount, setVisibleCoursesCount] = useState(COURSES_PER_PAGE);

  const handleSeeMore = () => {
    setVisibleCoursesCount(prevCount => prevCount + COURSES_PER_PAGE);
  };

  const coursesToDisplay = courses.slice(0, visibleCoursesCount);
  const hasMoreCourses = visibleCoursesCount < courses.length;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">All Courses</h1>
      <CourseListSection
        courses={coursesToDisplay}
        isLoading={isLoading}
      />
      {hasMoreCourses && (
        <div className="text-center mt-8">
          <button onClick={handleSeeMore} className="bg-purple-600 text-white px-6 py-3 rounded-md hover:bg-purple-700 text-lg font-semibold">See More Courses</button>
        </div>
      )}
    </div>
  );
};

export default AllCoursesPage;
