import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const EducationQualificationPage = ({ user }) => {
  const [qualifications, setQualifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (user && user.person_id) {
      fetchEducationQualifications();
    }
  }, [user]);

  const fetchEducationQualifications = async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/education-qualifications/${user.person_id}`);
      if (!response.ok) {
        throw new Error('Failed to fetch education qualifications');
      }
      const data = await response.json();
      setQualifications(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Not specified';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long'
    });
  };

  const getGradeColor = (grade) => {
    if (!grade) return 'bg-gray-100 text-gray-800';
    if (grade >= 4.0) return 'bg-green-100 text-green-800';
    if (grade >= 3.5) return 'bg-blue-100 text-blue-800';
    if (grade >= 3.0) return 'bg-yellow-100 text-yellow-800';
    return 'bg-red-100 text-red-800';
  };

  if (!user) {
    return (
      <div className="container mx-auto p-8 text-center">
        <p className="text-lg text-gray-600">You must be logged in to view this page.</p>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="container mx-auto p-8 text-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
        <p className="text-lg text-gray-600 mt-4">Loading education qualifications...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto p-8 text-center">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
          <p className="font-bold">Error</p>
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-4xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-4xl font-bold text-gray-800">Education Qualifications</h1>
          <Link 
            to="/profile" 
            className="bg-gray-600 text-white px-6 py-2 rounded-lg hover:bg-gray-700 transition duration-200"
          >
            Back to Profile
          </Link>
        </div>

        {qualifications.length === 0 ? (
          <div className="bg-white p-8 rounded-lg shadow-md text-center">
            <div className="text-gray-400 mb-4">
              <svg className="mx-auto h-16 w-16" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6.253v13m0-13C10.832 5.477 9.246 5 7.5 5S4.168 5.477 3 6.253v13C4.168 18.477 5.754 18 7.5 18s3.332.477 4.5 1.253m0-13C13.168 5.477 14.754 5 16.5 5c1.746 0 3.332.477 4.5 1.253v13C19.832 18.477 18.246 18 16.5 18c-1.746 0-3.332.477-4.5 1.253" />
              </svg>
            </div>
            <h3 className="text-xl font-semibold text-gray-700 mb-2">No Education Qualifications Found</h3>
            <p className="text-gray-600">You haven't added any education qualifications yet.</p>
          </div>
        ) : (
          <div className="space-y-6">
            {qualifications.map((qualification, index) => (
              <div key={index} className="bg-white p-6 rounded-lg shadow-md border-l-4 border-blue-500">
                <div className="flex justify-between items-start mb-4">
                  <div className="flex-1">
                    <h3 className="text-2xl font-bold text-gray-800 mb-2">
                      {qualification.degree}
                    </h3>
                    <p className="text-lg text-gray-600 mb-2">
                      <span className="font-semibold">Subject:</span> {qualification.subject}
                    </p>
                  </div>
                  {qualification.grade && (
                    <div className={`px-3 py-1 rounded-full text-sm font-medium ${getGradeColor(qualification.grade)}`}>
                      GPA: {qualification.grade}
                    </div>
                  )}
                </div>

                <div className="flex items-center text-gray-500">
                  <svg className="h-5 w-5 mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3a1 1 0 011-1h6a1 1 0 011 1v4h3a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2V9a2 2 0 012-2h3z" />
                  </svg>
                  <span className="font-medium">Completed:</span>
                  <span className="ml-2">{formatDate(qualification.passing_year)}</span>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Summary Statistics */}
        {qualifications.length > 0 && (
          <div className="bg-white p-6 rounded-lg shadow-md mt-8">
            <h2 className="text-2xl font-semibold text-gray-800 mb-6 border-b pb-2">
              Education Summary
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="text-center p-4 bg-blue-50 rounded-lg">
                <h3 className="text-lg font-semibold text-blue-800">Total Qualifications</h3>
                <p className="text-3xl font-bold text-blue-600">{qualifications.length}</p>
              </div>
              <div className="text-center p-4 bg-green-50 rounded-lg">
                <h3 className="text-lg font-semibold text-green-800">Average GPA</h3>
                <p className="text-3xl font-bold text-green-600">
                  {qualifications.filter(q => q.grade).length > 0 
                    ? (qualifications.reduce((sum, q) => sum + (q.grade || 0), 0) / qualifications.filter(q => q.grade).length).toFixed(2)
                    : 'N/A'
                  }
                </p>
              </div>
              <div className="text-center p-4 bg-purple-50 rounded-lg">
                <h3 className="text-lg font-semibold text-purple-800">Latest Degree</h3>
                <p className="text-lg font-bold text-purple-600">
                  {qualifications.length > 0 
                    ? qualifications.sort((a, b) => new Date(b.passing_year) - new Date(a.passing_year))[0].degree
                    : 'N/A'
                  }
                </p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EducationQualificationPage;
