import React, { useState, useRef, useEffect } from 'react';
import { BookOpen, Search, ShoppingCart, User, LogOut } from 'lucide-react';
import { Link, useNavigate } from 'react-router-dom';

const Navbar = ({ user, handleLogout }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/search?query=${encodeURIComponent(searchTerm.trim())}`);
    }
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(prev => !prev);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <header className="bg-gray-900 shadow-md sticky top-0 z-50">
      <div className="container mx-auto px-4 py-3 flex items-center justify-between">
        {/* Logo */}
        <Link to="/" className="flex items-center space-x-2">
          <BookOpen className="h-8 w-8 text-purple-600" />
          <span className="text-2xl font-bold text-white">LearnIT</span>
        </Link>
        {user && (
          <Link
            to="/my-courses"
            className="ml-6 border border-purple-600 text-purple-600 font-semibold text-lg px-6 py-2 rounded-full hover:bg-purple-600 hover:text-white transition-colors duration-200 shadow-sm"
          >
            My Courses
          </Link>
        )}

        {/* Search Bar */}
        <form onSubmit={handleSearch} className="hidden md:flex items-center border rounded-full w-1/3">
          <input
            type="text"
            placeholder="Search for anything..."
            className="w-full py-2 px-4 rounded-full focus:outline-none"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button type="submit" className="p-2 text-gray-300 hover:text-purple-400">
            <Search className="h-5 w-5" />
          </button>
        </form>

        {/* Navigation Links & Auth */}
        <div className="flex items-center space-x-6">
          <Link to="/all-courses" className="text-gray-300 hover:text-purple-400">Courses</Link>
          {user?.isAuthor ? (
            <Link to="/teacher-dashboard" className="text-gray-300 hover:text-purple-400">Teacher's Dashboard</Link>
          ) : (
            <Link to="/teach" className="text-gray-300 hover:text-purple-400">Teach on LearnIT</Link>
          )}
          <Link to="/cart" className="relative text-gray-300 hover:text-purple-400">
            <ShoppingCart className="h-6 w-6" />
            {/* You can add a cart item count here later */}
          </Link>

          <div className="flex items-center space-x-4">
            {user ? (
              <div className="relative" ref={dropdownRef}>
                <button
                  onClick={toggleDropdown}
                  className="flex items-center text-white hover:text-purple-400 font-semibold focus:outline-none"
                >
                  <User className="h-5 w-5 mr-1" />
                  {user.name}
                </button>
                {isDropdownOpen && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg py-1 z-20">
                    <Link
                      to="/profile"
                      onClick={() => setIsDropdownOpen(false)}
                      className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-100"
                    >
                      My Profile
                    </Link>
                    <button
                      onClick={() => { /* Add myCourses route if needed */ setIsDropdownOpen(false); }}
                      className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-100"
                    >
                      My Courses
                    </button>
                    <div className="border-t border-gray-200 my-1"></div>
                    <button
                      onClick={() => { handleLogout(); setIsDropdownOpen(false); }}
                      className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-100"
                    >
                      <LogOut className="h-4 w-4 mr-2 inline-block" /> Logout
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <>
                <Link to="/login" className="px-4 py-2 border border-gray-300 text-gray-300 font-semibold rounded-md hover:bg-gray-700 hover:text-white">
                  Log In
                </Link>
                <Link to="/signup" className="px-4 py-2 bg-purple-600 text-white font-semibold rounded-md hover:bg-purple-700">
                  Sign Up
                </Link>
              </>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
