import React, { useState } from 'react';
import { Sparkles, Loader2, AlertTriangle } from 'lucide-react';
import { callGeminiAPI } from '../utils/geminiApi';

const TeachPage = () => {
  const [courseTitle, setCourseTitle] = useState('');
  const [courseKeywords, setCourseKeywords] = useState('');
  const [generatedDescription, setGeneratedDescription] = useState('');
  const [isGenerating, setIsGenerating] = useState(false);
  const [error, setError] = useState('');

  const handleGenerateDescription = async () => {
    if (!courseTitle.trim()) {
      setError('Please enter a course title.');
      return;
    }
    setIsGenerating(true);
    setError('');
    setGeneratedDescription('');

    const prompt = `Generate a compelling and concise course description (around 100-150 words) for an online course titled "${courseTitle}". 
    The course focuses on the following keywords/topics: "${courseKeywords || 'general topics related to the title'}". 
    Highlight key benefits for students and what they will learn. Make it engaging.`;

    try {
      const description = await callGeminiAPI(prompt);
      setGeneratedDescription(description);
    } catch (err) {
      setError(err.message || 'Failed to generate description. Please try again.');
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Teach on CoursePlatform</h1>
      <div className="bg-white p-8 rounded-lg shadow-xl max-w-2xl mx-auto">
        <h2 className="text-2xl font-semibold text-gray-700 mb-2">AI Course Description Generator <Sparkles className="inline h-5 w-5 text-yellow-500" /></h2>
        <p className="text-gray-600 mb-6">Let our AI help you craft the perfect description for your new course!</p>
        
        <div className="space-y-4">
          <div>
            <label htmlFor="courseTitle" className="block text-sm font-medium text-gray-700 mb-1">Course Title <span className="text-red-500">*</span></label>
            <input 
              type="text" 
              id="courseTitle" 
              value={courseTitle} 
              onChange={(e) => setCourseTitle(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-md focus:ring-purple-500 focus:border-purple-500"
              placeholder="e.g., Introduction to Quantum Computing" 
            />
          </div>
          <div>
            <label htmlFor="courseKeywords" className="block text-sm font-medium text-gray-700 mb-1">Keywords/Topics (comma-separated)</label>
            <input 
              type="text" 
              id="courseKeywords" 
              value={courseKeywords} 
              onChange={(e) => setCourseKeywords(e.target.value)}
              className="w-full p-2 border border-gray-300 rounded-md focus:ring-purple-500 focus:border-purple-500"
              placeholder="e.g., qubits, superposition, entanglement, algorithms"
            />
          </div>
          <button 
            onClick={handleGenerateDescription}
            disabled={isGenerating}
            className="w-full bg-purple-600 text-white font-semibold py-2 px-4 rounded-md hover:bg-purple-700 disabled:bg-purple-300 flex items-center justify-center"
          >
            {isGenerating ? (
              <>
                <Loader2 className="animate-spin h-5 w-5 mr-2" /> Generating...
              </>
            ) : (
              <>
                <Sparkles className="h-5 w-5 mr-2" /> Generate Description
              </>
            )}
          </button>
        </div>

        {error && (
          <div className="mt-4 p-3 bg-red-100 text-red-700 border border-red-300 rounded-md flex items-center">
            <AlertTriangle className="h-5 w-5 mr-2" /> {error}
          </div>
        )}

        {generatedDescription && (
          <div className="mt-6">
            <h3 className="text-xl font-semibold text-gray-700 mb-2">Generated Description:</h3>
            <div className="p-4 bg-gray-50 border border-gray-200 rounded-md whitespace-pre-wrap text-gray-700">
              {generatedDescription}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default TeachPage;
