import React, { useState } from 'react';
import { BookOpen, ArrowLeft, User, Shield } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { fetchClient } from '../api/fetchClient'; // <-- Use the new client

const LoginPage = ({ handleLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [role, setRole] = useState('user');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({ ...prevState, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      // Use the new fetchClient here
      const data = await fetchClient('/api/v1/login', {
        method: 'POST',
        body: JSON.stringify({ ...formData, role }),
      });

      if (role === 'user' && data.role === 'admin') {
        throw new Error('Administrator accounts must use the "Admin" login tab.');
      }

      handleLogin(data.user, data.token, data.role);

      if (data.role === 'admin') {
        navigate('/admin/dashboard');
      } else {
        navigate('/');
      }

    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8 bg-white p-10 rounded-xl shadow-2xl relative pt-16">
        <button 
          onClick={() => navigate('/')} 
          className="absolute top-4 left-4 text-gray-500 hover:text-purple-600 flex items-center p-2 rounded-md bg-gray-100 hover:bg-gray-200 transition-colors"
        >
          <ArrowLeft className="h-5 w-5 mr-1" />
          Back to Home
        </button>
        
        <div>
          <BookOpen className="mx-auto h-12 w-auto text-purple-600" />
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Log in to your account
          </h2>
        </div>

        <div className="flex justify-center rounded-lg p-1 bg-gray-200">
            <button
                onClick={() => setRole('user')}
                className={`flex items-center justify-center w-full px-3 py-2 rounded-md text-sm font-medium transition-all ${
                role === 'user' 
                    ? 'bg-purple-600 text-white shadow-sm' 
                    : 'text-gray-600 hover:bg-gray-300'
                }`}
            >
                <User className="h-4 w-4 mr-2" /> User
            </button>
            <button
                onClick={() => setRole('admin')}
                className={`flex items-center justify-center w-full px-3 py-2 rounded-md text-sm font-medium transition-all ${
                role === 'admin' 
                    ? 'bg-purple-600 text-white shadow-sm' 
                    : 'text-gray-600 hover:bg-gray-300'
                }`}
            >
                <Shield className="h-4 w-4 mr-2" /> Admin
            </button>
        </div>

        {error && (
          <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 rounded-md" role="alert">
            <p className="font-bold">Error</p>
            <p>{error}</p>
          </div>
        )}
        
        <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
          <input type="hidden" name="remember" defaultValue="true" />
          <div className="rounded-md shadow-sm -space-y-px">
            <div>
              <label htmlFor="email-address" className="sr-only">Email address</label>
              <input id="email-address" name="email" type="email" autoComplete="email" required className="appearance-none rounded-none relative block w-full px-3 py-3 border border-gray-300 placeholder-gray-500 text-gray-900 bg-white rounded-t-md focus:outline-none focus:ring-purple-500 focus:border-purple-500 focus:z-10 sm:text-sm" placeholder="Email address" value={formData.email} onChange={handleChange} />
            </div>
            <div>
              <label htmlFor="password" className="sr-only">Password</label>
              <input id="password" name="password" type="password" autoComplete="current-password" required className="appearance-none rounded-none relative block w-full px-3 py-3 border border-gray-300 placeholder-gray-500 text-gray-900 bg-white rounded-b-md focus:outline-none focus:ring-purple-500 focus:border-purple-500 focus:z-10 sm:text-sm" placeholder="Password" value={formData.password} onChange={handleChange} />
            </div>
          </div>

          <div>
            <button type="submit" disabled={isLoading} className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-purple-600 hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-purple-500 disabled:bg-purple-400 disabled:cursor-not-allowed">
              {isLoading ? 'Logging in...' : 'Log In'}
            </button>
          </div>
        </form>
        
        {role !== 'admin' && (
          <p className="mt-2 text-center text-sm text-gray-600">
            Or{' '}
            <button onClick={() => navigate('/signup')} className="font-medium text-purple-600 hover:text-purple-500">
              create an account
            </button>
          </p>
        )}
      </div>
    </div>
  );
};

export default LoginPage;
