import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const MyCoursesPage = ({ user }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCourses = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setCourses([]);
        setLoading(false);
        return;
      }
      try {
        const response = await fetch('http://localhost:5001/api/my-courses', {
          headers: { 'Authorization': `Bearer ${token}` },
        });
        const data = await response.json();
        setCourses(data.courses || []);
      } catch (err) {
        setCourses([]);
      } finally {
        setLoading(false);
      }
    };
    fetchCourses();
  }, []);

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">My Courses</h1>
      {loading ? (
        <p>Loading your courses...</p>
      ) : courses.length === 0 ? (
        <p>You are not enrolled in any courses yet.</p>
      ) : (
        <ul className="space-y-4">
          {courses.map(course => (
            <li key={course.Course_ID} className="bg-white p-4 rounded-lg shadow flex items-center justify-between">
              <div>
                <Link to={`/my-courses/${course.Course_ID}/doing`} className="text-lg font-semibold text-purple-700 hover:underline">
                  {course.Title}
                </Link>
                <p className="text-gray-600 text-sm mt-1">{course.Description?.slice(0, 80)}{course.Description && course.Description.length > 80 ? '...' : ''}</p>
              </div>
              <span className="text-gray-500 text-sm">Enrolled</span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MyCoursesPage;
