import React, { useEffect } from 'react';
import { Target, Eye, Zap, Users, BookOpen, Award } from 'lucide-react';

const AboutUs = () => {
  // Effect for scroll animations
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('animate-fade-in-up');
          }
        });
      },
      {
        threshold: 0.1,
      }
    );

    const elements = document.querySelectorAll('.fade-in');
    elements.forEach((el) => observer.observe(el));

    return () => {
      elements.forEach((el) => observer.unobserve(el));
    };
  }, []);

  const teamMembers = [
    {
      name: 'Mohammad Sium',
      role: 'Co-Founder & Frontend Lead',
      imageUrl: '/sium.jpg',
      bio: 'The visionary behind our user experience, Sium crafts intuitive and beautiful interfaces that make learning a joy.',
    },
    {
      name: 'Atik Khan',
      role: 'Co-Founder & Backend Lead',
      imageUrl: '/atik.jpg',
      bio: 'The architect of our robust platform, Atik ensures everything runs smoothly, securely, and at scale.',
    },
  ];

  const timeline = [
    {
      year: '2023',
      title: 'The Idea',
      description: 'A small team of passionate developers and educators came together with a shared vision: to make quality education accessible to all.'
    },
    {
      year: '2024',
      title: 'Launch',
      description: 'After months of hard work, LearnIT was launched, offering a curated selection of courses in technology and business.'
    },
    {
      year: '2025',
      title: 'Growth',
      description: 'Our community grew to over 150,000 learners, and we expanded our course catalog to cover a wide range of disciplines.'
    },
    {
      year: 'Future',
      title: 'Innovation',
      description: 'We continue to innovate, exploring new ways to deliver engaging and effective learning experiences to our global community.'
    }
  ];

  return (
    <>
      <style>{`
        @keyframes fade-in-up {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        .animate-fade-in-up {
          animation: fade-in-up 1s ease-out forwards;
        }
        .fade-in {
          opacity: 0;
        }
        .timeline-line::before {
          content: '';
          position: absolute;
          top: 0;
          left: 50%;
          transform: translateX(-50%);
          width: 4px;
          height: 100%;
          background-color: #e5e7eb;
        }
        .timeline-point::before {
          content: '';
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          width: 1.25rem;
          height: 1.25rem;
          border-radius: 9999px;
          background-color: white;
          border: 4px solid #8b5cf6;
        }
      `}</style>
      <div className="bg-white text-gray-800">
        {/* Hero Section */}
        <section className="relative text-center py-28 md:py-40 bg-gray-900 text-white overflow-hidden">
          <div className="absolute inset-0 bg-gradient-to-r from-purple-900 via-gray-900 to-black opacity-80"></div>
          <div className="absolute inset-0 opacity-10" style={{backgroundImage: 'url(/path-to-your-abstract-background.svg)'}}></div>

          <div className="relative z-10 container mx-auto px-4 fade-in animate-fade-in-up">
            <h1 className="text-5xl md:text-7xl font-extrabold tracking-tight">
              Pioneering the Future of Education.
            </h1>
            <p className="mt-6 text-lg md:text-xl max-w-3xl mx-auto text-gray-300">
              We are a team of innovators, creators, and educators dedicated to building a world where anyone can learn anything, anytime.
            </p>
          </div>
        </section>
        
        {/* Mission Section */}
        <section className="py-20 md:py-28">
            <div className="container mx-auto px-4">
                <div className="grid md:grid-cols-2 gap-12 items-center">
                    <div className="fade-in">
                        <h2 className="text-4xl font-bold tracking-tight mb-4">Our Mission</h2>
                        <p className="text-lg text-gray-600 mb-6">To empower individuals worldwide by providing accessible, high-quality education that bridges the gap between ambition and achievement. We believe learning is a lifelong journey, and we're here to guide every step of the way.</p>
                        <div className="flex space-x-6">
                            <div className="flex items-center">
                                <Award className="h-8 w-8 text-purple-600 mr-3" />
                                <span className="text-lg font-semibold">Quality Content</span>
                            </div>
                            <div className="flex items-center">
                                <Users className="h-8 w-8 text-purple-600 mr-3" />
                                <span className="text-lg font-semibold">Expert Instructors</span>
                            </div>
                        </div>
                    </div>
                     <div className="fade-in" style={{ animationDelay: '200ms' }}>
                        <img src="https://placehold.co/600x400/8b5cf6/ffffff?text=LearnIT+Mission" alt="Our Mission" className="rounded-xl shadow-2xl"/>
                    </div>
                </div>
            </div>
        </section>

        {/* Timeline Section */}
        <section className="py-20 md:py-28 bg-gray-50">
            <div className="container mx-auto px-4">
                <div className="text-center mb-16">
                    <h2 className="text-4xl font-bold tracking-tight fade-in">Our Journey</h2>
                    <p className="mt-3 text-lg text-gray-600 max-w-2xl mx-auto fade-in" style={{ animationDelay: '100ms' }}>
                        From a simple idea to a global learning community.
                    </p>
                </div>
                <div className="relative timeline-line max-w-3xl mx-auto">
                    {timeline.map((item, index) => (
                        <div key={index} className="flex justify-center items-center mb-16 fade-in">
                            <div className={`w-1/2 ${index % 2 === 0 ? 'pr-8 text-right' : 'pl-8 text-left'}`}>
                                <p className="text-purple-600 font-semibold text-lg">{item.year}</p>
                                <h3 className="text-2xl font-bold mt-1">{item.title}</h3>
                                <p className="text-gray-600 mt-2">{item.description}</p>
                            </div>
                            <div className="w-12 h-12 absolute timeline-point"></div>
                             <div className={`w-1/2 ${index % 2 !== 0 ? 'pr-8 text-right' : 'pl-8 text-left'}`}>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>

        {/* Team Section */}
        <section className="py-20 md:py-28">
          <div className="container mx-auto px-4">
            <div className="text-center mb-16">
              <h2 className="text-4xl font-bold text-gray-900 tracking-tight fade-in">Meet The Founders</h2>
              <p className="mt-3 text-lg text-gray-600 max-w-2xl mx-auto fade-in" style={{ animationDelay: '100ms' }}>
                The passionate individuals who started it all.
              </p>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-10 max-w-4xl mx-auto">
              {teamMembers.map((member, index) => (
                <div key={index} className="bg-gray-50 rounded-lg shadow-xl overflow-hidden fade-in transform hover:scale-105 transition-transform duration-300" style={{ animationDelay: `${index * 150 + 200}ms` }}>
                  <div className="md:flex">
                    <div className="md:flex-shrink-0">
                      <img
                        className="h-56 w-full object-cover md:w-56"
                        src={member.imageUrl}
                        alt={member.name}
                      />
                    </div>
                    <div className="p-8 flex flex-col justify-center">
                      <div className="uppercase tracking-wide text-sm text-purple-600 font-semibold">{member.role}</div>
                      <h3 className="mt-1 text-2xl font-bold text-gray-900">{member.name}</h3>
                      <p className="mt-2 text-gray-600">{member.bio}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </section>
      </div>
    </>
  );
};

export default AboutUs;
