import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Loader, Save, XCircle, CheckCircle, MessageSquare } from 'lucide-react';

// A simple notification component for user feedback
const Notification = ({ message, type, onDismiss }) => {
  if (!message) return null;

  const baseStyle = "fixed top-5 right-5 p-4 rounded-lg shadow-lg flex items-center gap-3 z-50 transition-transform transform-gpu";
  const styles = {
    success: "bg-green-100 text-green-800",
    error: "bg-red-100 text-red-800",
  };

  return (
    <div className={`${baseStyle} ${styles[type]}`}>
      {type === 'success' ? <CheckCircle size={20} /> : <XCircle size={20} />}
      <span className="text-sm font-medium">{message}</span>
      <button onClick={onDismiss} className="text-gray-500 hover:text-gray-800 font-bold text-lg">&times;</button>
    </div>
  );
};

// The main component for the Course Marks Page
const CourseMarksPage = () => {
  const { courseId } = useParams();
  const [courseTitle, setCourseTitle] = useState('');
  const [students, setStudents] = useState([]);
  const [subTopics, setSubTopics] = useState([]);
  const [marks, setMarks] = useState({});
  const [comments, setComments] = useState({});
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [notification, setNotification] = useState({ message: '', type: '' });

  // Fetches all necessary data from the backend when the component mounts
  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        showNotification('Authentication token not found. Please log in.', 'error');
        setLoading(false);
        return;
      }
      try {
        setLoading(true);
        const response = await fetch(`http://localhost:5001/api/teacher/courses/${courseId}/marks`, {
          headers: { 'Authorization': `Bearer ${token}` },
        });
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setCourseTitle(data.courseTitle);
        setSubTopics(data.subTopics);
        setStudents(data.students);
        
        // Initialize local state for marks and comments from fetched data
        const initialMarks = {};
        const initialComments = {};
        data.students.forEach(student => {
          initialMarks[student.student_id] = {};
          initialComments[student.student_id] = {};
          data.subTopics.forEach(subTopic => {
            const markData = student.marks.find(m => m.sub_topic_id === subTopic.sub_topic_id);
            initialMarks[student.student_id][subTopic.sub_topic_id] = markData?.exam_mark ?? '';
            initialComments[student.student_id][subTopic.sub_topic_id] = markData?.comment ?? '';
          });
        });
        setMarks(initialMarks);
        setComments(initialComments);

      } catch (err) {
        console.error("Failed to fetch marks data", err);
        showNotification('Failed to load course data.', 'error');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [courseId]);

  // Helper function to display notifications
  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => {
      setNotification({ message: '', type: '' });
    }, 5000); // Notification disappears after 5 seconds
  };

  // Handlers for updating marks and comments in local state
  const handleMarkChange = (studentId, subTopicId, value) => {
    setMarks(prev => ({
      ...prev,
      [studentId]: { ...prev[studentId], [subTopicId]: value },
    }));
  };

  const handleCommentChange = (studentId, subTopicId, value) => {
    setComments(prev => ({
      ...prev,
      [studentId]: { ...prev[studentId], [subTopicId]: value },
    }));
  };

  // Submits all changes to the backend
  const handleSaveChanges = async () => {
    setSaving(true);
    const token = localStorage.getItem('token');
    const marksData = [];
    students.forEach(student => {
      subTopics.forEach(subTopic => {
        const mark = marks[student.student_id]?.[subTopic.sub_topic_id];
        const comment = comments[student.student_id]?.[subTopic.sub_topic_id];
        
        // Only include if there's a value to save to optimize payload
        if (mark !== undefined || comment !== undefined) {
          marksData.push({
            studentId: student.student_id,
            subTopicId: subTopic.sub_topic_id,
            mark: mark === '' ? null : Number(mark), // Send null if empty, otherwise convert to number
            comment: comment || null,
          });
        }
      });
    });

    try {
      const response = await fetch(`http://localhost:5001/api/teacher/courses/${courseId}/marks`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ marks: marksData }),
      });
      if (response.ok) {
        showNotification('Marks saved successfully!', 'success');
      } else {
        const errorData = await response.json();
        showNotification(errorData.error || 'Failed to save marks.', 'error');
      }
    } catch (err) {
      console.error("Error saving marks:", err);
      showNotification('An error occurred while saving marks.', 'error');
    } finally {
      setSaving(false);
    }
  };

  // Display a loading spinner while fetching data
  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen bg-gray-100">
        <Loader className="animate-spin h-10 w-10 text-indigo-600" />
      </div>
    );
  }

  // The main redesigned user interface
  return (
    <>
      <Notification 
        message={notification.message} 
        type={notification.type} 
        onDismiss={() => setNotification({ message: '', type: '' })} 
      />
      <div className="bg-gray-50 min-h-screen">
        <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {/* Page Header */}
          <header className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-8 gap-4">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 tracking-tight">{courseTitle}</h1>
              <p className="text-lg text-gray-600 mt-1">Manage Student Marks</p>
            </div>
            <button
              onClick={handleSaveChanges}
              disabled={saving || loading}
              className="inline-flex items-center justify-center px-6 py-3 border border-transparent text-base font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors"
            >
              {saving ? (
                <>
                  <Loader className="animate-spin -ml-1 mr-3 h-5 w-5" />
                  Saving...
                </>
              ) : (
                <>
                  <Save className="-ml-1 mr-2 h-5 w-5" />
                  Save Changes
                </>
              )}
            </button>
          </header>
          
          {/* Main Content Table */}
          <div className="shadow-lg rounded-xl overflow-hidden bg-white">
            <div className="overflow-x-auto">
              <table className="min-w-full">
                <thead className="bg-gray-100">
                  <tr>
                    <th scope="col" className="sticky left-0 bg-gray-100 px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider z-20">
                      Student
                    </th>
                    {subTopics.map(st => (
                      <th key={st.sub_topic_id} scope="col" className="px-6 py-4 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider whitespace-nowrap">
                        {st.title}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {students.map((student, studentIdx) => (
                    <tr key={student.student_id} className="group hover:bg-gray-50">
                      <td className="sticky left-0 bg-white group-hover:bg-gray-50 px-6 py-4 whitespace-nowrap z-10">
                        <div className="font-bold text-gray-900">{student.name}</div>
                        <div className="text-sm text-gray-500">{student.email}</div>
                      </td>
                      {subTopics.map(st => (
                        <td key={st.sub_topic_id} className="px-6 py-4 whitespace-nowrap">
                          <div className="flex flex-col gap-2">
                             <input
                                type="number"
                                placeholder="Mark"
                                value={marks[student.student_id]?.[st.sub_topic_id] ?? ''}
                                onChange={(e) => handleMarkChange(student.student_id, st.sub_topic_id, e.target.value)}
                                className="w-28 p-2 border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 text-sm"
                              />
                              <textarea
                                placeholder="Add a comment..."
                                value={comments[student.student_id]?.[st.sub_topic_id] ?? ''}
                                onChange={(e) => handleCommentChange(student.student_id, st.sub_topic_id, e.target.value)}
                                className="w-full min-w-[200px] mt-1 p-2 border border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 text-sm"
                                rows="2"
                              ></textarea>
                          </div>
                        </td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            {/* Display message if no students are enrolled */}
            {students.length === 0 && !loading && (
                <div className="text-center py-12 px-6">
                    <MessageSquare className="mx-auto h-12 w-12 text-gray-400" />
                    <h3 className="mt-2 text-sm font-medium text-gray-900">No Students Enrolled</h3>
                    <p className="mt-1 text-sm text-gray-500">There are currently no students enrolled in this course.</p>
                </div>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default CourseMarksPage;
