import React, { useState } from 'react';
import { BookOpen, Search, ShoppingCart, User, LogOut } from 'lucide-react';

const Navbar = ({ setCurrentPage, user, handleLogout }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      setCurrentPage('search', searchTerm.trim());
    }
  };

  return (
    <header className="bg-gray-900 shadow-md sticky top-0 z-50">
      <div className="container mx-auto px-4 py-3 flex items-center justify-between">
        {/* Logo */}
        <button onClick={() => setCurrentPage('home')} className="flex items-center space-x-2">
          <BookOpen className="h-8 w-8 text-purple-600" />
          <span className="text-2xl font-bold text-white">LearnIT</span>
        </button>

        {/* Search Bar */}
        <form onSubmit={handleSearch} className="hidden md:flex items-center border rounded-full w-1/3">
          <input
            type="text"
            placeholder="Search for anything..."
            className="w-full py-2 px-4 rounded-full focus:outline-none"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button type="submit" className="p-2 text-gray-300 hover:text-purple-400">
            <Search className="h-5 w-5" />
          </button>
        </form>

        {/* Navigation Links & Auth */}
        <div className="flex items-center space-x-6">
          <button onClick={() => setCurrentPage('allCourses')} className="text-gray-300 hover:text-purple-400">Courses</button>
          <button onClick={() => setCurrentPage('teach')} className="text-gray-300 hover:text-purple-400">Teach on LearnIT</button>
          <button onClick={() => setCurrentPage('cart')} className="relative text-gray-300 hover:text-purple-400">
            <ShoppingCart className="h-6 w-6" />
            {/* You can add a cart item count here later */}
          </button>

          <div className="flex items-center space-x-4">
            {user ? (
              <>
                <button onClick={() => setCurrentPage('profile')} className="flex items-center text-white hover:text-purple-400 font-semibold">
                  <User className="h-5 w-5 mr-1" />
                  {user.name}
                </button>
                <button onClick={handleLogout} className="flex items-center text-sm text-gray-300 hover:text-purple-400">
                  <LogOut className="h-4 w-4 mr-1" />
                  Logout
                </button>
              </>
            ) : (
              <>
                <button onClick={() => setCurrentPage('login')} className="px-4 py-2 border border-gray-300 text-gray-300 font-semibold rounded-md hover:bg-gray-700 hover:text-white">
                  Log In
                </button>
                <button onClick={() => setCurrentPage('signup')} className="px-4 py-2 bg-purple-600 text-white font-semibold rounded-md hover:bg-purple-700">
                  Sign Up
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
