import React, { useState } from 'react';
import { Plus, Trash2, FileText, PlayCircle, FileCheck2 } from 'lucide-react';

const emptySubtopic = () => ({
  title: '',
  videos: [{ title: '', link: '' }],
  assignments: [{ link: '' }],
  exams: [{ link: '' }],
});

const CreateCoursePage = () => {
  const [courseInfo, setCourseInfo] = useState({
    title: '',
    description: '',
    price: '',
  });
  const [subtopics, setSubtopics] = useState([emptySubtopic()]);

  const handleCourseChange = e => {
    setCourseInfo({ ...courseInfo, [e.target.name]: e.target.value });
  };

  const handleSubtopicChange = (idx, field, value) => {
    const updated = [...subtopics];
    updated[idx][field] = value;
    setSubtopics(updated);
  };

  const handleAddSubtopic = () => setSubtopics([...subtopics, emptySubtopic()]);
  const handleRemoveSubtopic = idx => setSubtopics(subtopics.filter((_, i) => i !== idx));

  const handleNestedChange = (type, sIdx, idx, field, value) => {
    const updated = [...subtopics];
    updated[sIdx][type][idx][field] = value;
    setSubtopics(updated);
  };

  const handleAddNested = (type, sIdx) => {
    const updated = [...subtopics];
    updated[sIdx][type].push(type === 'videos' ? { title: '', link: '' } : { link: '' });
    setSubtopics(updated);
  };
  const handleRemoveNested = (type, sIdx, idx) => {
    const updated = [...subtopics];
    updated[sIdx][type] = updated[sIdx][type].filter((_, i) => i !== idx);
    setSubtopics(updated);
  };

  const handleSubmit = e => {
    e.preventDefault();
    // TODO: Send to backend
    alert('Course creation not yet implemented.');
  };

  return (
    <div className="container mx-auto px-4 py-8 max-w-3xl">
      <h1 className="text-3xl font-bold mb-6 text-purple-700">Create a New Course</h1>
      <form onSubmit={handleSubmit} className="space-y-8">
        {/* Course Info */}
        <div className="bg-white p-6 rounded-lg shadow space-y-4">
          <h2 className="text-xl font-semibold mb-2">Course Info</h2>
          <input
            name="title"
            value={courseInfo.title}
            onChange={handleCourseChange}
            placeholder="Course Title"
            className="w-full p-3 border rounded mb-2"
            required
          />
          <textarea
            name="description"
            value={courseInfo.description}
            onChange={handleCourseChange}
            placeholder="Course Description"
            className="w-full p-3 border rounded mb-2"
            rows={4}
            required
          />
          <input
            name="price"
            value={courseInfo.price}
            onChange={handleCourseChange}
            placeholder="Price (USD)"
            type="number"
            min="0"
            className="w-40 p-3 border rounded"
            required
          />
        </div>
        {/* Subtopics */}
        <div className="bg-white p-6 rounded-lg shadow space-y-6">
          <div className="flex items-center justify-between mb-2">
            <h2 className="text-xl font-semibold">Subsections</h2>
            <button type="button" onClick={handleAddSubtopic} className="flex items-center gap-1 px-4 py-2 bg-purple-100 text-purple-700 rounded-full hover:bg-purple-200 font-semibold">
              <Plus className="h-4 w-4" /> Add Subsection
            </button>
          </div>
          {subtopics.map((sub, sIdx) => (
            <div key={sIdx} className="border rounded-lg p-4 mb-2 bg-purple-50">
              <div className="flex items-center gap-2 mb-2">
                <FileText className="h-5 w-5 text-purple-400" />
                <input
                  value={sub.title}
                  onChange={e => handleSubtopicChange(sIdx, 'title', e.target.value)}
                  placeholder="Subsection Title"
                  className="flex-1 p-2 border rounded"
                  required
                />
                <button type="button" onClick={() => handleRemoveSubtopic(sIdx)} className="ml-2 text-red-500 hover:text-red-700"><Trash2 className="h-5 w-5" /></button>
              </div>
              {/* Videos */}
              <div className="ml-6 mb-2">
                <div className="flex items-center gap-2 mb-1 font-medium text-gray-700"><PlayCircle className="h-4 w-4 text-purple-500" /> Videos</div>
                {sub.videos.map((v, idx) => (
                  <div key={idx} className="flex gap-2 mb-1">
                    <input
                      value={v.title}
                      onChange={e => handleNestedChange('videos', sIdx, idx, 'title', e.target.value)}
                      placeholder="Video Title"
                      className="flex-1 p-2 border rounded"
                      required
                    />
                    <input
                      value={v.link}
                      onChange={e => handleNestedChange('videos', sIdx, idx, 'link', e.target.value)}
                      placeholder="Video Link (embed URL)"
                      className="flex-1 p-2 border rounded"
                      required
                    />
                    <button type="button" onClick={() => handleRemoveNested('videos', sIdx, idx)} className="text-red-500 hover:text-red-700"><Trash2 className="h-5 w-5" /></button>
                  </div>
                ))}
                <button type="button" onClick={() => handleAddNested('videos', sIdx)} className="text-xs text-purple-600 hover:underline mt-1">+ Add Video</button>
              </div>
              {/* Assignments */}
              <div className="ml-6 mb-2">
                <div className="flex items-center gap-2 mb-1 font-medium text-gray-700"><FileCheck2 className="h-4 w-4 text-blue-500" /> Assignments</div>
                {sub.assignments.map((a, idx) => (
                  <div key={idx} className="flex gap-2 mb-1">
                    <input
                      value={a.link}
                      onChange={e => handleNestedChange('assignments', sIdx, idx, 'link', e.target.value)}
                      placeholder="Assignment Link (URL)"
                      className="flex-1 p-2 border rounded"
                      required
                    />
                    <button type="button" onClick={() => handleRemoveNested('assignments', sIdx, idx)} className="text-red-500 hover:text-red-700"><Trash2 className="h-5 w-5" /></button>
                  </div>
                ))}
                <button type="button" onClick={() => handleAddNested('assignments', sIdx)} className="text-xs text-blue-600 hover:underline mt-1">+ Add Assignment</button>
              </div>
              {/* Exams */}
              <div className="ml-6">
                <div className="flex items-center gap-2 mb-1 font-medium text-gray-700"><FileCheck2 className="h-4 w-4 text-red-500" /> Exams</div>
                {sub.exams.map((e, idx) => (
                  <div key={idx} className="flex gap-2 mb-1">
                    <input
                      value={e.link}
                      onChange={ev => handleNestedChange('exams', sIdx, idx, 'link', ev.target.value)}
                      placeholder="Exam Link (URL)"
                      className="flex-1 p-2 border rounded"
                      required
                    />
                    <button type="button" onClick={() => handleRemoveNested('exams', sIdx, idx)} className="text-red-500 hover:text-red-700"><Trash2 className="h-5 w-5" /></button>
                  </div>
                ))}
                <button type="button" onClick={() => handleAddNested('exams', sIdx)} className="text-xs text-red-600 hover:underline mt-1">+ Add Exam</button>
              </div>
            </div>
          ))}
        </div>
        <div className="text-right">
          <button type="submit" className="bg-purple-600 hover:bg-purple-700 text-white font-bold px-8 py-3 rounded-full shadow transition">Create Course</button>
        </div>
      </form>
    </div>
  );
};

export default CreateCoursePage;
