import React from 'react';
import { PlayCircle, BookOpen, Users, BarChart2, Globe } from 'lucide-react';
import { formatPrice } from '../utils/formatPrice';
import StarRating from '../components/StarRating'; // Corrected import path

const CourseDetailPage = ({ course, setCurrentPage, addToCart }) => {

  if (!course) return <div className="container mx-auto p-4">Course not found. <button onClick={() => setCurrentPage('home')} className="text-purple-600 hover:underline">Go Home</button></div>;
    // Function to handle adding to cart
  const handleAddToCartClick = () => {
    if (addToCart) {
      addToCart(course);
      // Optionally, show a confirmation or a small notification
      alert(`${course.title} added to cart!`); // Simple alert for demonstration
    }
  };

  // Function to handle buy now
  const handleBuyNowClick = () => {
    if (addToCart) {
      addToCart(course);
      setCurrentPage('cart'); // Navigate to cart page after adding
    }
  };

  return (
    <>
    <div className="bg-gray-800 text-white py-10">
        <div className="container mx-auto px-4">
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
                <div className="lg:col-span-2">
                    <button onClick={() => setCurrentPage('home')} className="text-sm text-purple-400 hover:underline mb-2">
                        &larr; Back to courses
                    </button>
                    <h1 className="text-3xl font-bold mb-2">{course.title}</h1>
                    <p className="text-lg text-gray-300 mb-4">{course.shortDescription || "A comprehensive course to elevate your skills."}</p>
                    <div className="flex items-center mb-2">
                        {course.bestseller && <span className="text-xs font-semibold bg-yellow-400 text-yellow-900 px-2 py-1 rounded mr-2">Bestseller</span>}
                        <StarRating rating={course.rating} reviews={course.reviews} />
                    </div>
                    <p className="text-sm text-gray-400 mb-4">Created by <span className="text-purple-400">{course.instructor}</span></p>
                    <div className="flex items-center space-x-4 text-sm text-gray-400 mb-6">
                        <span>Last updated: 05/2024</span>
                        <span><Globe className="h-4 w-4 inline mr-1" /> English</span>
                    </div>
                </div>

                <div className="lg:col-span-1">
                    <div className="bg-white text-gray-800 rounded-lg shadow-xl sticky top-24"> {/* Adjusted top for sticky navbar */}
                        <img src={course.imageUrl.replace('300x170', '360x200')} alt={course.title} className="w-full h-48 object-cover rounded-t-lg" />
                        <div className="p-6">
                            <div className="flex items-baseline mb-4">
                                <p className="text-3xl font-bold mr-2">{formatPrice(course.price)}</p>
                                {course.originalPrice && <p className="text-md text-gray-500 line-through">{formatPrice(course.originalPrice)}</p>}
                            </div>
                            <button onClick={handleAddToCartClick} className="w-full bg-purple-600 text-white font-bold py-3 rounded-md hover:bg-purple-700 transition duration-200 mb-3">
                                Add to Cart
                            </button>
                            <button onClick={handleBuyNowClick} className="w-full bg-transparent text-purple-700 border border-purple-700 font-bold py-3 rounded-md hover:bg-purple-50 transition duration-200 mb-4">
                                Buy Now
                            </button>
                            <p className="text-xs text-gray-500 text-center mb-3">30-Day Money-Back Guarantee</p>
                            <h4 className="font-semibold mb-2">This course includes:</h4>
                            <ul className="text-sm space-y-1 text-gray-600">
                                <li className="flex items-center"><PlayCircle className="h-4 w-4 mr-2 text-purple-600" /> 22 hours on-demand video</li>
                                <li className="flex items-center"><BookOpen className="h-4 w-4 mr-2 text-purple-600" /> 14 articles</li>
                                <li className="flex items-center"><Users className="h-4 w-4 mr-2 text-purple-600" /> Full lifetime access</li>
                                <li className="flex items-center"><BarChart2 className="h-4 w-4 mr-2 text-purple-600" /> Certificate of completion</li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
    {/* What you'll learn, requirements, description sections would go here */}
    <div className="container mx-auto px-4 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            <div className="lg:col-span-2">
                <div className="bg-white p-6 rounded-lg shadow-md mb-8">
                    <h2 className="text-2xl font-semibold mb-4">What you'll learn</h2>
                    <ul className="list-disc list-inside space-y-2 text-gray-700 columns-2">
                        <li>Build 16 web development projects for your portfolio, ready to apply for junior developer jobs.</li>
                        <li>Learn the latest technologies, including Javascript, React, Node and even Web3 development.</li>
                        <li>After the course you will be able to build ANY website you want.</li>
                        <li>Build fully-fledged websites and web apps for your startup or business.</li>
                        <li>Work as a freelance web developer.</li>
                        <li>Master backend development with Node</li>
                        <li>Master frontend development with React</li>
                        <li>Learn professional developer best practices.</li>
                    </ul>
                </div>

                <div className="bg-white p-6 rounded-lg shadow-md">
                    <h2 className="text-2xl font-semibold mb-3">Student Feedback & Reviews</h2>
                    {(course.reviewsList && course.reviewsList.length > 0) ? (
                        <>
                            <div className="space-y-4 max-h-96 overflow-y-auto pr-2">
                                {course.reviewsList.map(review => (
                                    <div key={review.id} className="border-b pb-3">
                                        <div className="flex items-center mb-1">
                                            <StarRating rating={review.rating} reviews={0} /> 
                                            <span className="ml-2 font-semibold">{review.user}</span>
                                        </div>
                                        <p className="text-gray-600 text-sm">{review.comment}</p>
                                    </div>
                                ))}
                            </div>
                        </>
                    ) : (
                        <p className="text-gray-600">No reviews yet for this course.</p>
                    )}
                </div>
            </div>
            {/* Instructor bio could go in the right column */}
        </div>
    </div>
    </>
  );
};

export default CourseDetailPage;
