import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Loader, Save, XCircle, CheckCircle } from 'lucide-react';

// A simple notification component
const Notification = ({ message, type, onDismiss }) => {
  if (!message) return null;

  const baseStyle = "fixed top-5 right-5 p-4 rounded-lg shadow-lg flex items-center gap-3";
  const styles = {
    success: "bg-green-100 text-green-800",
    error: "bg-red-100 text-red-800",
  };

  return (
    <div className={`${baseStyle} ${styles[type]}`}>
      {type === 'success' ? <CheckCircle /> : <XCircle />}
      <span>{message}</span>
      <button onClick={onDismiss} className="font-bold">X</button>
    </div>
  );
};

const CourseMarksPage = () => {
  const { courseId } = useParams();
  const [courseTitle, setCourseTitle] = useState('');
  const [students, setStudents] = useState([]);
  const [subTopics, setSubTopics] = useState([]);
  const [marks, setMarks] = useState({});
  const [comments, setComments] = useState({});
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [notification, setNotification] = useState({ message: '', type: '' });

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;
      try {
        const response = await fetch(`http://localhost:5001/api/teacher/courses/${courseId}/marks`, {
          headers: { 'Authorization': `Bearer ${token}` },
        });
        const data = await response.json();
        if (response.ok) {
          setCourseTitle(data.courseTitle);
          setSubTopics(data.subTopics);
          setStudents(data.students);
          
          const initialMarks = {};
          const initialComments = {};
          data.students.forEach(student => {
            initialMarks[student.student_id] = {};
            initialComments[student.student_id] = {};
            data.subTopics.forEach(subTopic => {
              const markData = student.marks.find(m => m.sub_topic_id === subTopic.sub_topic_id);
              initialMarks[student.student_id][subTopic.sub_topic_id] = markData ? markData.exam_mark || '' : '';
              initialComments[student.student_id][subTopic.sub_topic_id] = markData ? markData.comment || '' : '';
            });
          });
          setMarks(initialMarks);
          setComments(initialComments);

        }
      } catch (err) {
        console.error("Failed to fetch marks data", err);
        showNotification('Failed to load course data.', 'error');
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [courseId]);

  const showNotification = (message, type) => {
    setNotification({ message, type });
    setTimeout(() => {
      setNotification({ message: '', type: '' });
    }, 5000); // Notification disappears after 5 seconds
  };

  const handleMarkChange = (studentId, subTopicId, value) => {
    setMarks(prev => ({
      ...prev,
      [studentId]: {
        ...prev[studentId],
        [subTopicId]: value,
      },
    }));
  };

  const handleCommentChange = (studentId, subTopicId, value) => {
    setComments(prev => ({
      ...prev,
      [studentId]: {
        ...prev[studentId],
        [subTopicId]: value,
      },
    }));
  };

  const handleSaveChanges = async () => {
    setSaving(true);
    const token = localStorage.getItem('token');
    const marksData = [];
    students.forEach(student => {
        subTopics.forEach(subTopic => {
            const mark = marks[student.student_id]?.[subTopic.sub_topic_id];
            const comment = comments[student.student_id]?.[subTopic.sub_topic_id];
            if (mark !== undefined || comment !== undefined) {
                 marksData.push({
                    studentId: student.student_id,
                    subTopicId: subTopic.sub_topic_id,
                    mark: mark || null,
                    comment: comment || null,
                });
            }
        });
    });

    try {
      const response = await fetch(`http://localhost:5001/api/teacher/courses/${courseId}/marks`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ marks: marksData }),
      });
      if (response.ok) {
        showNotification('Marks saved successfully!', 'success');
      } else {
        showNotification('Failed to save marks.', 'error');
      }
    } catch (err) {
      showNotification('Error saving marks.', 'error');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return <div className="flex justify-center items-center h-screen"><Loader className="animate-spin h-8 w-8" /></div>;
  }

  return (
    <>
      <Notification 
        message={notification.message} 
        type={notification.type} 
        onDismiss={() => setNotification({ message: '', type: '' })} 
      />
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-6">
          <div>
              <h1 className="text-3xl font-bold">Manage Marks</h1>
              <p className="text-xl text-gray-600">{courseTitle}</p>
          </div>
          <button
            onClick={handleSaveChanges}
            disabled={saving}
            className="bg-green-600 hover:bg-green-700 text-white font-bold px-6 py-2 rounded-full shadow transition flex items-center gap-2 disabled:bg-gray-400"
          >
            <Save className="h-5 w-5" /> {saving ? 'Saving...' : 'Save Changes'}
          </button>
        </div>
        
        <div className="overflow-x-auto bg-white rounded-lg shadow">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider sticky left-0 bg-gray-50 z-10">Student</th>
                {subTopics.map(st => (
                  <th key={st.sub_topic_id} className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">{st.title}</th>
                ))}
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {students.map(student => (
                <tr key={student.student_id}>
                  <td className="px-6 py-4 whitespace-nowrap sticky left-0 bg-white z-10">
                    <div className="font-semibold">{student.name}</div>
                    <div className="text-sm text-gray-500">{student.email}</div>
                  </td>
                  {subTopics.map(st => (
                    <td key={st.sub_topic_id} className="px-6 py-4 whitespace-nowrap">
                      <input
                        type="number"
                        placeholder="Mark"
                        value={marks[student.student_id]?.[st.sub_topic_id] || ''}
                        onChange={(e) => handleMarkChange(student.student_id, st.sub_topic_id, e.target.value)}
                        className="w-24 p-1 border rounded"
                      />
                      <textarea
                        placeholder="Comment..."
                        value={comments[student.student_id]?.[st.sub_topic_id] || ''}
                        onChange={(e) => handleCommentChange(student.student_id, st.sub_topic_id, e.target.value)}
                        className="w-full mt-2 p-1 border rounded text-sm"
                        rows="2"
                      ></textarea>
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};

export default CourseMarksPage;
