import React from 'react';
import { useSearchParams } from 'react-router-dom';
import CourseCard from '../components/CourseCard'; // Assuming CourseCard is in a shared components folder

const SearchPage = ({ courses }) => {
  const [searchParams] = useSearchParams();
  const searchTerm = searchParams.get('query') || '';

  const filteredCourses = courses.filter(course =>
    (course.Title || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
    (course.Description || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
    (course.instructor || '').toLowerCase().includes(searchTerm.toLowerCase())
  );

  const hasResults = filteredCourses.length > 0;

  return (
    <div className="bg-gray-50 min-h-screen">
      <div className="container mx-auto px-4 py-12">
        <div className="text-center mb-12">
          <h1 className="text-4xl md:text-5xl font-extrabold text-gray-900">
            Search Results for "{searchTerm}"
          </h1>
        </div>

        {hasResults ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
            {filteredCourses.map(course => (
              <CourseCard key={course.Course_ID} course={course} />
            ))}
          </div>
        ) : (
          <div className="text-center py-20">
            <p className="text-gray-600 text-lg">No courses found matching your search.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchPage;
