import React from 'react';
import { BookOpen, Globe, Facebook, Twitter, Instagram } from 'lucide-react';

// Footer Component
const Footer = () => {
  const aboutLinks = [
    { name: 'Contact Us', url: '#' }, // Placeholder URL
    { name: 'About Us', url: '#' },   // Placeholder URL
  ];

  const socialMediaLinks = [
    { name: 'Facebook', icon: Facebook, url: 'https://www.facebook.com/yourplatform' },
    { name: 'Twitter', icon: Twitter, url: 'https://twitter.com/yourplatform' },
    { name: 'Instagram', icon: Instagram, url: 'https://instagram.com/yourplatform' },
  ];

  return (
    <footer className="bg-gray-800 text-gray-300 pt-12 pb-8">
      <div className="container mx-auto px-4">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 mb-8">
          {/* About Section */}
          <div>
            <h5 className="font-semibold text-white mb-3">About</h5>
            <ul>
              {aboutLinks.map((link, index) => (
                <li key={index} className="mb-2">
                  {/* Using <a> for navigation, even with placeholder #. For production, use actual URLs. */}
                  <a href={link.url} className="hover:text-white hover:underline text-sm">{link.name}</a>
                </li>
              ))}
            </ul>
          </div>

          {/* Social Media Section */}
          <div>
            <h5 className="font-semibold text-white mb-3">Follow Us</h5>
            <div className="flex space-x-4">
              {socialMediaLinks.map((social, index) => {
                const IconComponent = social.icon;
                return (
                  <a key={index} href={social.url} target="_blank" rel="noopener noreferrer" className="text-gray-400 hover:text-white">
                    <IconComponent className="h-6 w-6" />
                  </a>
                );
              })}
            </div> {/* This closes the div for social media links */}
          </div>
          <div className="md:col-span-2 lg:col-span-1 lg:justify-self-end">
             <button className="border border-gray-300 hover:border-white text-white px-4 py-2 rounded-md flex items-center text-sm">
                <Globe className="h-5 w-5 mr-2" /> English
             </button>
          </div>
        </div>
        <div className="flex flex-col md:flex-row justify-between items-center border-t border-gray-700 pt-8">
          <div className="flex items-center mb-4 md:mb-0">
            <BookOpen className="h-8 w-8 text-purple-400" />
            <span className="ml-2 text-xl font-bold text-white">CoursePlatform</span>
          </div>
          <p className="text-sm">&copy; {new Date().getFullYear()} CoursePlatform, Inc.</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
