import React, { useState, useRef } from 'react';
import { Search, ShoppingCart, Menu, X, BookOpen } from 'lucide-react';

// Navigation Component
const Navbar = ({ setCurrentPage }) => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const searchInputRef = useRef(null);

  const handleSearch = () => {
    // Only search if the ref is attached and there's a non-empty value
    if (searchInputRef.current && searchInputRef.current.value.trim()) {
      setCurrentPage('search', searchInputRef.current.value);
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      event.preventDefault(); // Prevent default form submission behavior
      handleSearch();
      if (isMobileMenuOpen) {
        setIsMobileMenuOpen(false);
      }
    }
  };

  const navLinkClasses = "text-gray-300 hover:text-white px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200";
  const mobileNavLinkClasses = "block w-full text-left px-3 py-2 rounded-md text-base font-medium text-gray-300 hover:text-white hover:bg-gray-800 transition-colors duration-200";
  const searchInputClasses = "block w-full bg-gray-800 border border-gray-600 rounded-full py-2 pl-10 pr-12 text-sm text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-purple-500 focus:bg-gray-700 transition-colors";
  const searchButtonClasses = "absolute right-1.5 top-1/2 transform -translate-y-1/2 bg-purple-600 text-white p-1.5 rounded-full hover:bg-purple-700 transition-colors duration-200";

  return (
    <nav className="bg-gray-900 border-b border-gray-700 sticky top-0 z-50">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          <div className="flex items-center">
            <button onClick={() => setCurrentPage('home')} className="flex-shrink-0 flex items-center">
              <BookOpen className="h-8 w-8 text-purple-500" />
              <span className="ml-2 text-xl font-bold text-white hidden md:inline">learnIT</span>
            </button>
            <div className="hidden md:block ml-6">
              <button type="button" onClick={() => setCurrentPage('allCourses')} className={navLinkClasses}>
                All Courses
              </button>
            </div>
          </div>

          <div className="hidden md:flex flex-grow max-w-xl mx-4">
            <div className="relative w-full">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Search className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type="text"
                ref={searchInputRef}
                onKeyDown={handleKeyDown}
                placeholder="Search for anything"
                className={searchInputClasses}
              />
              <button
                type="button"
                onClick={handleSearch}
                className={searchButtonClasses}
              >
                <Search className="h-4 w-4" />
              </button>
            </div>
          </div>

          <div className="hidden md:flex items-center space-x-2">
            <button type="button" onClick={() => setCurrentPage('teach')} className={navLinkClasses}>Teach on Platform</button>
            <button onClick={() => setCurrentPage('cart')} className="text-gray-300 hover:text-white p-2 rounded-full transition-colors duration-200">
              <ShoppingCart className="h-6 w-6" />
            </button>
            <button onClick={() => setCurrentPage('login')} className="text-white bg-transparent border border-gray-600 hover:bg-gray-700 px-4 py-2 rounded-md text-sm font-semibold transition-colors duration-200">Log In</button>
            <button onClick={() => setCurrentPage('signup')} className="bg-purple-600 text-white hover:bg-purple-700 px-4 py-2 rounded-md text-sm font-semibold transition-colors duration-200">Sign Up</button>
          </div>

          <div className="md:hidden flex items-center">
            <button onClick={() => setCurrentPage('cart')} className="text-gray-300 hover:text-white mr-2 p-2 rounded-full transition-colors duration-200">
              <ShoppingCart className="h-6 w-6" />
            </button>
            <button onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)} className="text-gray-300 hover:text-white p-2 rounded-full transition-colors duration-200">
              {isMobileMenuOpen ? <X className="h-6 w-6" /> : <Menu className="h-6 w-6" />}
            </button>
          </div>
        </div>
      </div>

      {isMobileMenuOpen && (
        <div className="md:hidden bg-gray-900 border-t border-gray-700 pb-4">
          <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
            <div className="relative mb-2 px-2">
              <div className="absolute inset-y-0 left-0 pl-5 flex items-center pointer-events-none">
                <Search className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type="text"
                ref={searchInputRef}
                onKeyDown={handleKeyDown}
                placeholder="Search for anything"
                className={searchInputClasses}
              />
              <button type="button"
                onClick={() => { handleSearch(); setIsMobileMenuOpen(false); }}
                className={searchButtonClasses}
              >
                <Search className="h-4 w-4" />
              </button>
            </div>
            <button type="button" onClick={() => {setCurrentPage('allCourses'); setIsMobileMenuOpen(false);}} className={mobileNavLinkClasses}>All Courses</button>
            <button type="button" onClick={() => {setCurrentPage('teach'); setIsMobileMenuOpen(false);}} className={mobileNavLinkClasses}>Teach on Platform</button>
          </div>
          <div className="pt-4 pb-3 border-t border-gray-700">
            <div className="flex flex-col px-4 space-y-2">
              <button onClick={() => {setCurrentPage('login'); setIsMobileMenuOpen(false);}} className="w-full text-center text-white bg-transparent border border-gray-600 hover:bg-gray-700 px-4 py-2 rounded-md text-sm font-semibold transition-colors duration-200">Log In</button>
              <button onClick={() => {setCurrentPage('signup'); setIsMobileMenuOpen(false);}} className="w-full text-center bg-purple-600 text-white hover:bg-purple-700 px-4 py-2 rounded-md text-sm font-semibold transition-colors duration-200">Sign Up</button>
            </div>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
