import React from 'react';
// Removed Search import as the search bar is being removed

// Hero Section Component
const HeroSection = () => {
  return (
    <div className="bg-gray-100"> {/* Removed vertical padding to eliminate gap */}
      {/* The image will now directly fill this section from left to right */}
      <img
            src="/Homepage_logo.png" 
            alt="Learning illustration" 
            className="w-full h-auto object-cover" // Make image full width, auto height, cover space
            // Removed absolute positioning and specific sizing/transforms
          />
    </div>
  );
};

export default HeroSection;
