import React, { useState } from 'react';

const TeachPage = ({ user, token, onRoleUpdate }) => {
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleConfirm = async () => {
    setIsLoading(true);
    setError('');
    setSuccess('');

    try {
      const response = await fetch('http://localhost:5001/api/users/become-teacher', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.msg || 'Failed to become a teacher.');
      }
      
      setSuccess(data.msg);
      // Update the user state in the parent App component
      if (onRoleUpdate) {
        onRoleUpdate(data.user);
      }
      
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
      setShowConfirmation(false);
    }
  };

  const renderContent = () => {
    if (!user) {
      return (
        <p className="text-gray-600 mb-8">
          You must be logged in to become a teacher. Please log in or sign up first.
        </p>
      );
    }

    if (user.role === 'instructor' || success) {
      return (
        <>
          <h2 className="text-2xl font-semibold text-green-700 mb-4">You are an Instructor!</h2>
          <p className="text-gray-600 mb-8">
            You can now start creating and managing your courses.
          </p>
          {/* You could add a link to an instructor dashboard here */}
        </>
      );
    }
    
    return (
      <>
        <h2 className="text-2xl font-semibold text-gray-700 mb-4">Become an Instructor</h2>
        <p className="text-gray-600 mb-8">
          Share your knowledge and reach millions of students around the world. We provide the tools and skills to teach what you love.
        </p>
        <button 
          onClick={() => setShowConfirmation(true)}
          className="bg-purple-600 text-white px-8 py-3 rounded-md hover:bg-purple-700 text-lg font-semibold transition-colors"
        >
          Become a Teacher
        </button>
      </>
    );
  };

  return (
    <>
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">Teach on LearnIT</h1>
        <div className="bg-white p-8 rounded-lg shadow-md max-w-2xl mx-auto text-center">
          {error && <p className="text-red-500 mb-4">{error}</p>}
          {success && !error && <p className="text-green-600 mb-4">{success}</p>}
          {renderContent()}
        </div>
      </div>

      {showConfirmation && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex justify-center items-center z-50" onClick={() => !isLoading && setShowConfirmation(false)}>
          <div className="bg-white p-8 rounded-lg shadow-xl max-w-md w-full m-4" onClick={e => e.stopPropagation()}>
            <h3 className="text-2xl font-bold text-gray-800 mb-4">Confirm Your Decision</h3>
            <p className="text-gray-600 mb-6">Are you sure you want to register as an instructor? This will allow you to create and manage your own courses on LearnIT.</p>
            <div className="flex justify-end space-x-4">
              <button onClick={() => setShowConfirmation(false)} disabled={isLoading} className="px-6 py-2 rounded-md font-semibold text-gray-700 bg-gray-200 hover:bg-gray-300 transition-colors disabled:opacity-50">Cancel</button>
              <button onClick={handleConfirm} disabled={isLoading} className="px-6 py-2 rounded-md font-semibold text-white bg-purple-600 hover:bg-purple-700 transition-colors disabled:bg-purple-400 disabled:cursor-not-allowed">
                {isLoading ? 'Confirming...' : 'Confirm'}
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default TeachPage;
