import React, { useState, useEffect } from 'react';
import { Plus, Trash2, FileText, PlayCircle, FileCheck2, Image, Video } from 'lucide-react';
import { useNavigate, useParams } from 'react-router-dom';

const emptySubtopic = () => ({ /* ... */ });

const EditCoursePage = () => {
    const navigate = useNavigate();
    const { courseId } = useParams();
    const [loading, setLoading] = useState(true);
    const [courseInfo, setCourseInfo] = useState({
        title: '',
        description: '',
        price: '',
        thumbnailUrl: '', // Add state for thumbnail
        trailerLink: '',  // Add state for trailer
    });
    const [subtopics, setSubtopics] = useState([emptySubtopic()]);
    // ... (other states)

    useEffect(() => {
        const fetchCourseData = async () => {
            // ... (fetch logic)
            try {
                // ...
                const data = await response.json();
                const course = data.course;
                
                setCourseInfo({
                    title: course.title,
                    description: course.description,
                    price: course.price,
                    thumbnailUrl: course.thumbnailUrl || '', // Set thumbnail from API
                    trailerLink: course.trailerLink || '',   // Set trailer from API
                });

                // ... (subtopic logic)
            } catch (error) {
                // ...
            } finally {
                setLoading(false);
            }
        };
        fetchCourseData();
    }, [courseId, navigate]);

    const handleCourseChange = e => {
        setCourseInfo({ ...courseInfo, [e.target.name]: e.target.value });
    };

    const handleSubmit = async e => {
        e.preventDefault();
        // ... (validation)

        const requestBody = { ...courseInfo, subtopics }; // courseInfo now includes new fields
        const token = localStorage.getItem('token');
        
        try {
            const response = await fetch(`http://localhost:5001/api/teacher/courses/${courseId}`, {
                method: 'PUT',
                // ... (headers and body)
                body: JSON.stringify(requestBody),
            });
            // ... (response handling)
        } catch (err) {
            // ... (error handling)
        }
    };

    if (loading) return <div>Loading...</div>;

    return (
        <div className="container mx-auto px-4 py-12 max-w-3xl">
            <div className="bg-gradient-to-r from-purple-100 to-white rounded-2xl shadow-xl p-8">
                <h1 className="text-4xl font-extrabold mb-8 text-purple-700 text-center">Edit Course</h1>
                <form onSubmit={handleSubmit} className="space-y-10">
                    {/* Course Info */}
                    <div className="bg-white/90 p-6 rounded-xl shadow-md space-y-4">
                         <h2 className="text-2xl font-bold text-purple-700">Course Details</h2>
                        {/* Title & Price */}
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <input name="title" value={courseInfo.title} onChange={handleCourseChange} placeholder="Course Title" required className="w-full p-3 border rounded-lg" />
                            <input name="price" value={courseInfo.price} onChange={handleCourseChange} placeholder="Price (USD)" type="number" min="0" required className="w-full p-3 border rounded-lg" />
                        </div>
                        {/* Thumbnail & Trailer */}
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                           <div className="relative">
                                <Image className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-gray-400" />
                                <input name="thumbnailUrl" value={courseInfo.thumbnailUrl} onChange={handleCourseChange} placeholder="Thumbnail Image URL" className="w-full p-3 pl-10 border rounded-lg" />
                           </div>
                           <div className="relative">
                                <Video className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-gray-400" />
                                <input name="trailerLink" value={courseInfo.trailerLink} onChange={handleCourseChange} placeholder="Trailer Video URL" className="w-full p-3 pl-10 border rounded-lg" />
                           </div>
                        </div>
                        {/* Description */}
                        <textarea name="description" value={courseInfo.description} onChange={handleCourseChange} placeholder="Course Description" rows={4} required className="w-full p-3 border rounded-lg" />
                    </div>

                    {/* Subtopics Section */}
                    {/* ... (The rest of the form for adding/removing subtopics) ... */}

                    <div className="text-right mt-8">
                        <button type="submit" className="bg-purple-600 text-white font-bold px-10 py-3 rounded-full shadow-lg">Save Changes</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default EditCoursePage;
