import React, { useState, useEffect } from 'react';

// --- Import Your Components ---
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import HeroSection from './components/HeroSection';
import CourseListSection from './components/CourseListSection';
import ContactUs from './components/ContactUs';
// --- You will create and import these other pages as you build them ---
// import AboutUs from './components/AboutUs';
// import CourseDetail from './components/CourseDetail';
// import Login from './components/Login';
// import Signup from './components/Signup';

// --- Mock Data for Demonstration ---
const mockCourses = [
  { id: 1, title: 'The Complete 2024 Web Development Bootcamp', instructor: 'Dr. Angela Yu', rating: 4.7, reviews: 277153, price: 16.99, originalPrice: 84.99, imageUrl: 'https://img-c.udemycdn.com/course/480x270/1565838_e54e_18.jpg', bestseller: true },
  { id: 2, title: 'React - The Complete Guide (incl Hooks, React Router, Redux)', instructor: 'Maximilian Schwarzmüller', rating: 4.6, reviews: 178070, price: 16.99, originalPrice: 84.99, imageUrl: 'https://img-c.udemycdn.com/course/480x270/1362070_b9a1_2.jpg', bestseller: true },
  { id: 3, title: 'The Complete JavaScript Course 2024: From Zero to Expert!', instructor: 'Jonas Schmedtmann', rating: 4.7, reviews: 189411, price: 16.99, originalPrice: 84.99, imageUrl: 'https://img-c.udemycdn.com/course/480x270/851712_fc63_20.jpg', bestseller: false },
  { id: 4, title: 'Python for Data Science and Machine Learning Bootcamp', instructor: 'Jose Portilla', rating: 4.6, reviews: 134588, price: 16.99, originalPrice: 84.99, imageUrl: 'https://img-c.udemycdn.com/course/480x270/903744_8eb2.jpg', bestseller: false },
];

// A simple placeholder for pages that are not yet created
const PlaceholderPage = ({ pageName, data }) => (
    <div className="container mx-auto p-8 text-center min-h-[50vh] flex flex-col justify-center">
        <h1 className="text-3xl font-bold mb-4">This is the {pageName} page.</h1>
        <p className="text-gray-600">This component has not been created yet.</p>
        {data && <pre className="mt-4 bg-gray-100 p-4 rounded-md text-left">{JSON.stringify(data, null, 2)}</pre>}
    </div>
);

function App() {
  // State to manage which page is currently displayed
  const [currentPage, setCurrentPage] = useState('home');
  // State to pass data between pages (e.g., course details, search term)
  const [pageData, setPageData] = useState(null);
  // State for user authentication (null if logged out)
  const [user, setUser] = useState(null);
  // State for loading courses
  const [isLoading, setIsLoading] = useState(false);
  const [courses, setCourses] = useState([]);

  // --- 1. Page Navigation Handler ---
  // This function is passed to Navbar and Footer to control the app's view
  const handleSetCurrentPage = (page, data = null) => {
    setCurrentPage(page);
    setPageData(data);
    window.scrollTo(0, 0); // Scroll to top on every page change
  };

  // --- Mock Authentication ---
  const handleLogout = () => {
    setUser(null);
    handleSetCurrentPage('home');
  };

  // --- Mock Data Fetching ---
  useEffect(() => {
    if (currentPage === 'home' || currentPage === 'allCourses') {
        setIsLoading(true);
        setTimeout(() => {
            setCourses(mockCourses);
            setIsLoading(false);
        }, 1000); // Simulate network delay
    }
  }, [currentPage]);


  // --- 2. Page Rendering Logic ---
  // This switch statement determines which component to show
  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return (
          <>
            <HeroSection />
            <div id="courses-section">
              <CourseListSection
                title="A broad selection of courses"
                courses={courses}
                setCurrentPage={handleSetCurrentPage}
                isLoading={isLoading}
              />
            </div>
          </>
        );

      case 'contact':
        return <ContactUs />;

      case 'about':
        // return <AboutUs />;
        return <PlaceholderPage pageName="About Us" />;

      case 'courseDetail':
        // return <CourseDetail course={pageData} />;
        return <PlaceholderPage pageName="Course Detail" data={pageData} />;

      case 'allCourses':
        return (
            <div id="courses-section">
              <CourseListSection
                title="All Courses"
                courses={courses}
                setCurrentPage={handleSetCurrentPage}
                isLoading={isLoading}
              />
            </div>
        );

      // Add other cases for pages from your Navbar/Footer
      case 'login':
        return <PlaceholderPage pageName="Login" />;
      case 'signup':
        return <PlaceholderPage pageName="Sign Up" />;
      case 'profile':
        return <PlaceholderPage pageName="My Profile" />;
      case 'myCourses':
        return <PlaceholderPage pageName="My Courses" />;
      case 'cart':
        return <PlaceholderPage pageName="Shopping Cart" />;
      case 'teach':
        return <PlaceholderPage pageName="Teach on LearnIT" />;
      case 'search':
        return <PlaceholderPage pageName={`Search Results for "${pageData}"`} />;

      default:
        return <PlaceholderPage pageName="Home (Default)" />;
    }
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-50">
      {/* 3. Pass the handler and user state to the Navbar */}
      <Navbar 
        setCurrentPage={handleSetCurrentPage} 
        user={user}
        handleLogout={handleLogout}
      />

      <main className="flex-grow">
        {renderPage()}
      </main>

      {/* 4. Pass the handler to the Footer */}
      <Footer setCurrentPage={handleSetCurrentPage} />
    </div>
  );
}

export default App;
