import React from 'react';
import CourseListSection from '../components/CourseListSection';

const SearchPage = ({ searchTerm, courses, setCurrentPage }) => {
  const filteredCourses = courses.filter(course =>
    course.title.toLowerCase().includes(searchTerm.toLowerCase()) || // Condition 1
    (course.description && course.description.toLowerCase().includes(searchTerm.toLowerCase())) || // Condition 2 (explicitly grouped)
    course.instructor.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">
        Search Results for "{searchTerm}"
      </h1>
      <CourseListSection
        courses={filteredCourses}
        setCurrentPage={setCurrentPage}
      />
    </div>
  );
};

export default SearchPage;
