import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { PlayCircle, FileText, FileCheck2, ChevronDown, ChevronUp, Menu } from 'lucide-react';

// Helper to ensure links are absolute
const ensureAbsoluteUrl = url =>
  url && (url.startsWith('http://') || url.startsWith('https://')) ? url : `https://${url}`;

const highlightBg = 'bg-gradient-to-r from-purple-100 via-purple-50 to-white';
const highlightBorder = 'border-l-4 border-purple-500';

const CourseDoingPage = () => {
  const { courseId } = useParams();
  const navigate = useNavigate();
  const [subtopics, setSubtopics] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [openDropdown, setOpenDropdown] = useState(null);
  const [selectedVideo, setSelectedVideo] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;
      try {
        const response = await fetch(`http://localhost:5001/api/courses/${courseId}/doing`, {
          headers: { 'Authorization': `Bearer ${token}` },
        });
        const data = await response.json();
        setSubtopics(data.subtopics || []);
        // Auto-select first video
        for (const sub of data.subtopics || []) {
          if (sub.videos && sub.videos.length > 0) {
            setSelectedVideo(sub.videos[0]);
            break;
          }
        }
      } catch (err) {
        setSubtopics([]);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [courseId]);

  const handleDropdown = (id) => {
    setOpenDropdown(openDropdown === id ? null : id);
  };

  return (
    <div className="flex min-h-screen bg-gradient-to-br from-gray-50 to-purple-50">
      {/* Sidebar */}
      <div className={`fixed z-30 inset-y-0 left-0 w-80 bg-gradient-to-b from-purple-50 via-white to-purple-100 border-r shadow-lg transform transition-transform duration-300 md:static md:translate-x-0 h-full overflow-y-auto ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} md:block`}>
        <div className="flex items-center justify-between px-6 py-5 border-b bg-gradient-to-r from-purple-100 to-white">
          <h2 className="text-xl font-bold text-purple-700 tracking-wide">Course Content</h2>
          <button className="md:hidden text-gray-500 hover:text-purple-600" onClick={() => setSidebarOpen(false)}>
            ✕
          </button>
        </div>
        {loading ? <p className="p-4 text-gray-500">Loading...</p> : (
          <ul className="space-y-2 p-4">
            {subtopics.map(sub => (
              <li key={sub.subTopicId} className={openDropdown === sub.subTopicId ? `${highlightBg} ${highlightBorder} rounded-lg` : ''}>
                <button
                  className={`w-full flex items-center justify-between font-semibold text-purple-800 px-3 py-2 rounded-lg transition ${openDropdown === sub.subTopicId ? 'bg-purple-100' : 'hover:bg-purple-50'}`}
                  onClick={() => handleDropdown(sub.subTopicId)}
                >
                  <span className="flex items-center gap-2">
                    <FileText className="h-5 w-5 text-purple-400" />
                    {sub.title}
                  </span>
                  {openDropdown === sub.subTopicId ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
                </button>
                {openDropdown === sub.subTopicId && (
                  <div className="ml-6 mt-2 space-y-2">
                    {sub.videos.length > 0 && <div className="font-medium text-gray-700 flex items-center gap-2"><PlayCircle className="h-4 w-4 text-purple-500" />Videos</div>}
                    {sub.videos.map(video => (
                      <button
                        key={video.Video_ID}
                        className={`flex items-center gap-2 w-full text-left px-2 py-1 rounded-lg transition ${selectedVideo && selectedVideo.Video_ID === video.Video_ID ? 'bg-purple-300 text-purple-900 font-bold shadow' : 'hover:bg-purple-100 text-purple-700'}`}
                        onClick={() => setSelectedVideo(video)}
                      >
                        <PlayCircle className="h-4 w-4 text-purple-400" />
                        {video.Title || 'Untitled Video'}
                      </button>
                    ))}
                    {sub.assignments.length > 0 && <div className="font-medium text-gray-700 mt-2 flex items-center gap-2"><FileCheck2 className="h-4 w-4 text-blue-500" />Assignments</div>}
                    {sub.assignments.map(ass => (
                      <a
                        key={ass.Assignment_ID}
                        href={ensureAbsoluteUrl(ass.Assignment_Link)}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="flex items-center gap-2 w-full text-left px-2 py-1 rounded-lg hover:bg-blue-100 text-blue-700 transition"
                      >
                        <FileCheck2 className="h-4 w-4 text-blue-400" />
                        Assignment {ass.Assignment_ID}: {ass.Assignment_Link}
                      </a>
                    ))}
                    {sub.exams.length > 0 && <div className="font-medium text-gray-700 mt-2 flex items-center gap-2"><FileCheck2 className="h-4 w-4 text-red-500" />Exams</div>}
                    {sub.exams.map(exam => (
                      <a
                        key={exam.Exam_ID}
                        href={ensureAbsoluteUrl(exam.Exam_Link)}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="flex items-center gap-2 w-full text-left px-2 py-1 rounded-lg hover:bg-red-100 text-red-700 transition"
                      >
                        <FileCheck2 className="h-4 w-4 text-red-400" />
                        Exam {exam.Exam_ID}: {exam.Exam_Link}
                      </a>
                    ))}
                  </div>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
      {/* Sidebar toggle button for mobile */}
      <button
        className="fixed top-4 left-4 z-40 md:hidden bg-purple-600 text-white px-4 py-2 rounded-full shadow-lg"
        onClick={() => setSidebarOpen(true)}
        style={{ display: sidebarOpen ? 'none' : 'block' }}
      >
        <Menu className="inline-block mr-2 h-5 w-5" /> Menu
      </button>
      {/* Main content */}
      <div className="flex-1 flex flex-col items-center justify-center p-8 ml-0 md:ml-80">
        <div className="w-full max-w-3xl flex justify-end mb-4">
          <button
            className="bg-yellow-400 hover:bg-yellow-500 text-yellow-900 font-bold px-6 py-2 rounded-full shadow transition"
            onClick={() => navigate(`/my-courses/${courseId}/notices`)}
          >
            Notice Board
          </button>
        </div>
        <div className="w-full max-w-3xl">
          {selectedVideo ? (
            <>
              <div className="aspect-w-16 aspect-h-9 bg-black rounded-xl overflow-hidden mb-6 shadow-lg border border-gray-200">
                <iframe
                  src={selectedVideo.Link}
                  title={selectedVideo.Title || 'Course Video'}
                  allowFullScreen
                  className="w-full h-96 rounded-xl"
                />
              </div>
              <h2 className="text-2xl font-bold mb-2 text-gray-800">{selectedVideo.Title}</h2>
              {selectedVideo.Description && <p className="text-gray-600 mb-4">{selectedVideo.Description}</p>}
            </>
          ) : (
            <div className="text-gray-500 text-lg text-center py-16">Select a video from the sidebar to start learning.</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CourseDoingPage;
