import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

// Helper to ensure links are absolute
const ensureAbsoluteUrl = url =>
  url && (url.startsWith('http://') || url.startsWith('https://')) ? url : `https://${url}`;

const CourseDoingPage = () => {
  const { courseId } = useParams();
  const [subtopics, setSubtopics] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [openDropdown, setOpenDropdown] = useState(null);
  const [selectedVideo, setSelectedVideo] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;
      try {
        const response = await fetch(`http://localhost:5001/api/courses/${courseId}/doing`, {
          headers: { 'Authorization': `Bearer ${token}` },
        });
        const data = await response.json();
        setSubtopics(data.subtopics || []);
        // Auto-select first video
        for (const sub of data.subtopics || []) {
          if (sub.videos && sub.videos.length > 0) {
            setSelectedVideo(sub.videos[0]);
            break;
          }
        }
      } catch (err) {
        setSubtopics([]);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [courseId]);

  const handleDropdown = (id) => {
    setOpenDropdown(openDropdown === id ? null : id);
  };

  return (
    <div className="flex min-h-screen bg-gray-50">
      {/* Sidebar */}
      <div className={`bg-white border-r w-72 p-4 transition-transform duration-300 z-20 ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'} fixed md:static md:translate-x-0 h-full overflow-y-auto`}>
        <button className="md:hidden mb-4 text-purple-600 font-bold" onClick={() => setSidebarOpen(false)}>Close</button>
        <h2 className="text-xl font-bold mb-4">Course Content</h2>
        {loading ? <p>Loading...</p> : (
          <ul className="space-y-2">
            {subtopics.map(sub => (
              <li key={sub.subTopicId}>
                <button
                  className="w-full text-left font-semibold text-purple-700 hover:underline flex justify-between items-center py-2"
                  onClick={() => handleDropdown(sub.subTopicId)}
                >
                  {sub.title}
                  <span>{openDropdown === sub.subTopicId ? '▲' : '▼'}</span>
                </button>
                {openDropdown === sub.subTopicId && (
                  <div className="ml-4 space-y-2">
                    {sub.videos.length > 0 && <div className="font-medium text-gray-700">Videos:</div>}
                    {sub.videos.map(video => (
                      <button
                        key={video.Video_ID}
                        className={`block w-full text-left px-2 py-1 rounded hover:bg-purple-100 ${selectedVideo && selectedVideo.Video_ID === video.Video_ID ? 'bg-purple-200 font-bold' : ''}`}
                        onClick={() => setSelectedVideo(video)}
                      >
                        {video.Title || 'Untitled Video'}
                      </button>
                    ))}
                    {sub.assignments.length > 0 && <div className="font-medium text-gray-700 mt-2">Assignments:</div>}
                    {sub.assignments.map(ass => (
                      <a
                        key={ass.Assignment_ID}
                        href={ensureAbsoluteUrl(ass.Assignment_Link)}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="block w-full text-left px-2 py-1 rounded hover:bg-blue-100 text-blue-700"
                      >
                        Assignment {ass.Assignment_ID}: {ass.Assignment_Link}
                      </a>
                    ))}
                    {sub.exams.length > 0 && <div className="font-medium text-gray-700 mt-2">Exams:</div>}
                    {sub.exams.map(exam => (
                      <a
                        key={exam.Exam_ID}
                        href={ensureAbsoluteUrl(exam.Exam_Link)}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="block w-full text-left px-2 py-1 rounded hover:bg-red-100 text-red-700"
                      >
                        Exam {exam.Exam_ID}: {exam.Exam_Link}
                      </a>
                    ))}
                  </div>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
      {/* Sidebar toggle button for mobile */}
      <button
        className="fixed top-4 left-4 z-30 md:hidden bg-purple-600 text-white px-4 py-2 rounded-full shadow-lg"
        onClick={() => setSidebarOpen(true)}
        style={{ display: sidebarOpen ? 'none' : 'block' }}
      >
        Open Menu
      </button>
      {/* Main content */}
      <div className="flex-1 flex flex-col items-center justify-center p-8 ml-0 md:ml-72">
        {selectedVideo ? (
          <div className="w-full max-w-3xl">
            <div className="aspect-w-16 aspect-h-9 bg-black rounded-lg overflow-hidden mb-4">
              <iframe
                src={selectedVideo.Link}
                title={selectedVideo.Title || 'Course Video'}
                allowFullScreen
                className="w-full h-96 rounded-lg"
              />
            </div>
            <h2 className="text-2xl font-bold mb-2">{selectedVideo.Title}</h2>
          </div>
        ) : (
          <div className="text-gray-500">Select a video from the sidebar to start learning.</div>
        )}
      </div>
    </div>
  );
};

export default CourseDoingPage;
