import React from 'react';

const HomePage = ({ setCurrentPage }) => {
  return (
    <div>
      {/* Hero Section */}
      <div className="relative h-screen bg-cover bg-center" style={{ backgroundImage: "url('https://images.unsplash.com/photo-1522202176988-66273c2fd55f?q=80&w=2071&auto=format&fit=crop')" }}>
        <div className="absolute inset-0 bg-black opacity-50"></div>
        <div className="relative z-10 flex flex-col items-start justify-center h-full text-white p-8 md:p-20 max-w-3xl">
          <h1 className="text-4xl md:text-6xl font-bold mb-4 leading-tight">Unlock Your Potential</h1>
          <p className="text-lg md:text-2xl mb-8">Choose from over 200,000 online video courses with new additions published every month.</p>
        </div>
      </div>

      {/* Content Section */}
      <div className="container mx-auto px-6 py-16">
        <h2 className="text-3xl font-bold text-center mb-12">Featured Courses</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {/* Placeholder for course cards */}
          {[1, 2, 3, 4].map(i => (
            <div key={i} className="bg-white rounded-lg shadow-lg overflow-hidden transform hover:-translate-y-2 transition-transform duration-300">
              <div className="h-48 bg-gray-300"></div>
              <div className="p-6">
                <h3 className="font-bold text-lg mb-2">Course Title {i}</h3>
                <p className="text-gray-600 text-sm">Author Name</p>
                <div className="mt-4 flex items-center">
                  <span className="text-yellow-500 font-bold">4.5</span>
                  <span className="ml-2 text-gray-500">(1,234)</span>
                </div>
                <p className="mt-2 font-bold text-lg">$19.99</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
