import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Pencil, Eye, Trash2, FileText, PlusCircle, AlertTriangle, LoaderCircle } from 'lucide-react';

// A simple, reusable modal component for confirmations.
const ConfirmationModal = ({ isOpen, onClose, onConfirm, title, message }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-60 backdrop-blur-sm" aria-labelledby="modal-title" role="dialog" aria-modal="true">
      <div className="bg-white w-full max-w-md p-6 rounded-2xl shadow-xl m-4">
        <div className="flex items-start">
          <div className="mx-auto flex-shrink-0 flex items-center justify-center h-12 w-12 rounded-full bg-red-100 sm:mx-0 sm:h-10 sm:w-10">
            <AlertTriangle className="h-6 w-6 text-red-600" aria-hidden="true" />
          </div>
          <div className="ml-4 text-left">
            <h3 className="text-lg leading-6 font-bold text-gray-900" id="modal-title">
              {title}
            </h3>
            <div className="mt-2">
              <p className="text-sm text-gray-600">
                {message}
              </p>
            </div>
          </div>
        </div>
        <div className="mt-6 sm:mt-8 sm:flex sm:flex-row-reverse gap-3">
          <button
            type="button"
            className="w-full inline-flex justify-center rounded-md border border-transparent shadow-sm px-4 py-2 bg-red-600 text-base font-medium text-white hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 sm:w-auto sm:text-sm transition-colors"
            onClick={onConfirm}
          >
            Confirm Delete
          </button>
          <button
            type="button"
            className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 sm:mt-0 sm:w-auto sm:text-sm transition-colors"
            onClick={onClose}
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};


const TeacherDashboardPage = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [deleting, setDeleting] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [courseToDelete, setCourseToDelete] = useState(null);
  const navigate = useNavigate();

  // --- Core Logic (Unchanged) ---
  useEffect(() => {
    const fetchCourses = async () => {
      // Using a placeholder for the token for this example.
      // In a real app, this would come from your auth context or storage.
      const token = localStorage.getItem('token') || 'dummy-token';
      if (!token) {
        setLoading(false);
        return;
      }
      try {
        // Simulating API call
        // const response = await fetch('http://localhost:5001/api/teacher/my-courses', {
        //   headers: { 'Authorization': `Bearer ${token}` },
        // });
        // const data = await response.json();
        // setCourses(data.courses || []);
        
        // Mock data for demonstration since the API is not available.
        setTimeout(() => {
          setCourses([
            { Course_ID: 1, Title: "Introduction to Web Development", Description: "Learn the fundamentals of HTML, CSS, and JavaScript to build modern websites." },
            { Course_ID: 2, Title: "Advanced React Patterns", Description: "Dive deep into React hooks, state management, and performance optimization." },
            { Course_ID: 3, Title: "Data Structures in Python", Description: "Master essential data structures and algorithms using Python for problem-solving." },
          ]);
          setLoading(false);
        }, 1500);

      } catch (err) {
        console.error("Failed to fetch courses:", err);
        setCourses([]);
        setLoading(false);
      }
    };
    fetchCourses();
  }, []);

  const handleDelete = async (courseId) => {
    setDeleting(courseId);
    const token = localStorage.getItem('token');
    try {
      // Simulating API call
      // const response = await fetch(`http://localhost:5001/api/courses/${courseId}`, {
      //   method: 'DELETE',
      //   headers: { 'Authorization': `Bearer ${token}` },
      // });
      // if (response.ok) {
      //   setCourses(courses => courses.filter(c => c.Course_ID !== courseId));
      // } else {
      //   // Replace alert with a more modern notification system (e.g., toast)
      //   console.error('Failed to delete course.');
      // }
      
      // Mocking API deletion
      await new Promise(resolve => setTimeout(resolve, 1000));
      setCourses(courses => courses.filter(c => c.Course_ID !== courseId));

    } catch (err) {
      console.error('Error deleting course:', err);
    } finally {
      setDeleting(null);
      closeDeleteModal();
    }
  };
  // --- End of Core Logic ---

  const openDeleteModal = (courseId) => {
    setCourseToDelete(courseId);
    setIsModalOpen(true);
  };

  const closeDeleteModal = () => {
    setCourseToDelete(null);
    setIsModalOpen(false);
  };

  const confirmDelete = () => {
    if (courseToDelete) {
      handleDelete(courseToDelete);
    }
  };

  const ActionButton = ({ to, icon: Icon, children, className, ...props }) => {
    const content = (
      <>
        <Icon className="h-4 w-4" />
        <span>{children}</span>
      </>
    );
    const baseClasses = "flex items-center justify-center gap-2 px-3 py-2 text-sm font-medium rounded-lg transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2";
    
    if (to) {
      return (
        <Link to={to} className={`${baseClasses} ${className}`} {...props}>
          {content}
        </Link>
      );
    }
    return (
      <button className={`${baseClasses} ${className}`} {...props}>
        {content}
      </button>
    );
  };

  if (loading) {
    return (
      <div className="flex flex-col items-center justify-center min-h-[60vh] text-gray-500">
        <LoaderCircle className="h-12 w-12 animate-spin text-indigo-500" />
        <p className="mt-4 text-lg font-semibold">Loading Your Courses...</p>
      </div>
    );
  }

  return (
    <div className="bg-gray-50 min-h-screen">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Page Header */}
        <header className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Teacher's Dashboard</h1>
            <p className="mt-1 text-md text-gray-600">Manage your courses, notices, and student interactions.</p>
          </div>
          <button
            className="mt-4 sm:mt-0 flex items-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white font-bold px-5 py-2.5 rounded-lg shadow-sm transition-all duration-200"
            onClick={() => navigate('/teacher/courses/create')}
          >
            <PlusCircle className="h-5 w-5" />
            Create Course
          </button>
        </header>

        {/* Courses Grid */}
        {courses.length === 0 ? (
          <div className="text-center py-16 px-6 bg-white rounded-xl border border-dashed border-gray-300">
            <h2 className="text-xl font-semibold text-gray-800">No Courses Found</h2>
            <p className="mt-2 text-gray-500">You haven't created any courses yet. Get started now!</p>
            <button
              className="mt-6 inline-flex items-center gap-2 bg-indigo-600 hover:bg-indigo-700 text-white font-bold px-5 py-2.5 rounded-lg shadow-sm transition-all duration-200"
              onClick={() => navigate('/teacher/courses/create')}
            >
              <PlusCircle className="h-5 w-5" />
              Create Your First Course
            </button>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {courses.map(course => (
              <div key={course.Course_ID} className="bg-white rounded-xl shadow-md border border-gray-200 flex flex-col transition-all duration-300 hover:shadow-xl hover:-translate-y-1">
                <div className="p-6 flex-grow">
                  <h3 className="font-bold text-lg text-gray-900">{course.Title}</h3>
                  <p className="text-gray-600 text-sm mt-2 h-16">
                    {course.Description?.slice(0, 100)}{course.Description && course.Description.length > 100 ? '...' : ''}
                  </p>
                </div>
                <div className="bg-gray-50 p-3 border-t border-gray-200 grid grid-cols-2 sm:grid-cols-4 gap-2">
                  <ActionButton 
                    to={`/teacher/courses/${course.Course_ID}/edit`} 
                    icon={Pencil} 
                    className="text-blue-700 bg-blue-50 hover:bg-blue-100 focus:ring-blue-500"
                    title="Edit Course"
                  >
                    Edit
                  </ActionButton>
                  <ActionButton 
                    to={`/teacher/courses/${course.Course_ID}/notices`}
                    icon={FileText} 
                    className="text-purple-700 bg-purple-50 hover:bg-purple-100 focus:ring-purple-500"
                    title="Manage Notices"
                  >
                    Notices
                  </ActionButton>
                   <ActionButton 
                    to={`/course/${course.Course_ID}`}
                    icon={Eye} 
                    className="text-green-700 bg-green-50 hover:bg-green-100 focus:ring-green-500"
                    title="View Course"
                  >
                    View
                  </ActionButton>
                  <button
                    onClick={() => openDeleteModal(course.Course_ID)}
                    className="flex items-center justify-center gap-2 px-3 py-2 text-sm font-medium rounded-lg transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 text-red-700 bg-red-50 hover:bg-red-100 focus:ring-red-500"
                    disabled={deleting === course.Course_ID}
                    title="Delete Course"
                  >
                    {deleting === course.Course_ID ? (
                      <LoaderCircle className="h-4 w-4 animate-spin"/>
                    ) : (
                      <Trash2 className="h-4 w-4" />
                    )}
                    <span>{deleting === course.Course_ID ? '...' : 'Delete'}</span>
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
      <ConfirmationModal
        isOpen={isModalOpen}
        onClose={closeDeleteModal}
        onConfirm={confirmDelete}
        title="Delete Course"
        message="Are you sure you want to delete this course? This action is permanent and cannot be undone."
      />
    </div>
  );
};

export default TeacherDashboardPage;
