import React, { useEffect } from 'react';
import { Target, Eye, Zap, Users, BookOpen, Award } from 'lucide-react';

const AboutUs = () => {
  // Effect for scroll animations
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('animate-fade-in-up');
          }
        });
      },
      {
        threshold: 0.1,
      }
    );

    const elements = document.querySelectorAll('.fade-in');
    elements.forEach((el) => observer.observe(el));

    return () => {
      elements.forEach((el) => observer.unobserve(el));
    };
  }, []);

  const teamMembers = [
    {
      name: 'Mohammad Sium',
      role: 'Co-Founder & Frontend Lead',
      imageUrl: '/sium.jpg',
      bio: 'The visionary behind our user experience, Sium crafts intuitive and beautiful interfaces that make learning a joy.',
    },
    {
      name: 'Atik Khan',
      role: 'Co-Founder & Backend Lead',
      imageUrl: '/atik.jpg',
      bio: 'The architect of our robust platform, Atik ensures everything runs smoothly, securely, and at scale.',
    },
  ];

  const values = [
    {
      icon: <Target className="h-10 w-10 text-white" />,
      title: 'Our Mission',
      description: 'To democratize education by providing accessible, high-quality learning experiences that empower individuals to achieve their goals.',
    },
    {
      icon: <Eye className="h-10 w-10 text-white" />,
      title: 'Our Vision',
      description: 'To build a global community of lifelong learners, connected by a passion for knowledge and personal growth.',
    },
    {
      icon: <Zap className="h-10 w-10 text-white" />,
      title: 'Our Approach',
      description: 'We blend expert-led content with cutting-edge technology to create practical, engaging, and effective courses.',
    },
  ];

  const stats = [
    { icon: <BookOpen className="h-8 w-8 text-purple-600" />, value: '1,200+', label: 'Courses Offered' },
    { icon: <Users className="h-8 w-8 text-purple-600" />, value: '150,000+', label: 'Students Enrolled' },
    { icon: <Award className="h-8 w-8 text-purple-600" />, value: '4.8/5', label: 'Average Rating' },
  ];

  return (
    <>
      <style>{`
        @keyframes fade-in-up {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        .animate-fade-in-up {
          animation: fade-in-up 0.8s ease-out forwards;
        }
        .fade-in {
          opacity: 0;
        }
      `}</style>
      <div className="bg-white text-gray-800">
        {/* Hero Section */}
        <section className="relative text-center py-24 md:py-32 bg-purple-600 text-white overflow-hidden">
          <div className="absolute inset-0 bg-gradient-to-r from-purple-700 to-purple-500 opacity-80"></div>
          <div className="absolute -bottom-1/4 -left-1/4 w-1/2 h-1/2 bg-white/10 rounded-full filter blur-3xl"></div>
          <div className="absolute -top-1/4 -right-1/4 w-1/2 h-1/2 bg-white/10 rounded-full filter blur-3xl"></div>
          <div className="relative z-10 container mx-auto px-4 fade-in animate-fade-in-up">
            <h1 className="text-4xl md:text-6xl font-extrabold tracking-tight">
              We're Changing The Way People Learn.
            </h1>
            <p className="mt-6 text-lg md:text-xl max-w-3xl mx-auto text-purple-200">
              LearnIT is more than a platform; it's a community dedicated to unlocking human potential through accessible and effective education.
            </p>
          </div>
        </section>

        {/* Stats Section */}
        <section className="py-16 bg-gray-50">
          <div className="container mx-auto px-4">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8 text-center">
              {stats.map((stat, index) => (
                <div key={index} className="fade-in" style={{ animationDelay: `${index * 150}ms` }}>
                  <div className="flex justify-center items-center mb-3">{stat.icon}</div>
                  <p className="text-4xl font-bold text-gray-900">{stat.value}</p>
                  <p className="text-md text-gray-500">{stat.label}</p>
                </div>
              ))}
            </div>
          </div>
        </section>

        {/* Our Values Section */}
        <section className="py-20 md:py-28">
          <div className="container mx-auto px-4">
            <div className="text-center mb-16">
              <h2 className="text-4xl font-bold text-gray-900 tracking-tight fade-in">Our Core Values</h2>
              <p className="mt-3 text-lg text-gray-600 max-w-2xl mx-auto fade-in" style={{ animationDelay: '100ms' }}>
                The principles that guide our mission and shape our culture.
              </p>
            </div>
            <div className="grid md:grid-cols-3 gap-8">
              {values.map((value, index) => (
                <div key={index} className="bg-purple-600 text-white p-8 rounded-xl shadow-lg transform hover:-translate-y-2 transition-transform duration-300 fade-in" style={{ animationDelay: `${index * 150 + 200}ms` }}>
                  <div className="flex items-center justify-center h-16 w-16 rounded-full bg-white/20 mb-6">
                    {value.icon}
                  </div>
                  <h3 className="text-2xl font-semibold mb-3">{value.title}</h3>
                  <p className="text-purple-200">{value.description}</p>
                </div>
              ))}
            </div>
          </div>
        </section>

        {/* Team Section */}
        <section className="bg-gray-50 py-20 md:py-28">
          <div className="container mx-auto px-4">
            <div className="text-center mb-16">
              <h2 className="text-4xl font-bold text-gray-900 tracking-tight fade-in">Meet The Founders</h2>
              <p className="mt-3 text-lg text-gray-600 max-w-2xl mx-auto fade-in" style={{ animationDelay: '100ms' }}>
                The passionate individuals who started it all.
              </p>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-10 max-w-4xl mx-auto">
              {teamMembers.map((member, index) => (
                <div key={index} className="bg-white rounded-lg shadow-xl overflow-hidden fade-in" style={{ animationDelay: `${index * 150 + 200}ms` }}>
                  <div className="md:flex">
                    <div className="md:flex-shrink-0">
                      <img
                        className="h-48 w-full object-cover md:w-48"
                        src={member.imageUrl}
                        alt={member.name}
                      />
                    </div>
                    <div className="p-8">
                      <div className="uppercase tracking-wide text-sm text-purple-600 font-semibold">{member.role}</div>
                      <h3 className="mt-1 text-2xl font-bold text-gray-900">{member.name}</h3>
                      <p className="mt-2 text-gray-600">{member.bio}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </section>
      </div>
    </>
  );
};

export default AboutUs;
