import React, { useState, useEffect } from 'react';
import { Plus, Trash2, FileText, PlayCircle, FileCheck2, Image, Video } from 'lucide-react';
import { useNavigate, useParams } from 'react-router-dom';

const emptySubtopic = () => ({
  title: '',
  videos: [{ title: '', link: '' }],
  assignments: [{ link: '' }],
  exams: [{ link: '' }],
});

const EditCoursePage = () => {
    const navigate = useNavigate();
    const { courseId } = useParams();
    const [loading, setLoading] = useState(true);
    const [courseInfo, setCourseInfo] = useState({
        title: '',
        description: '',
        price: '',
        thumbnailUrl: '', // This field is not used by the backend but kept for potential future use
        trailerLink: '',
    });
    const [subtopics, setSubtopics] = useState([emptySubtopic()]);
    const [finalQuizExamLink, setFinalQuizExamLink] = useState('');


    useEffect(() => {
        const fetchCourseData = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                navigate('/login');
                return;
            }
            try {
                setLoading(true);
                const response = await fetch(`http://localhost:5001/api/teacher/courses/${courseId}`, {
                    headers: { 'Authorization': `Bearer ${token}` },
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch course data.');
                }

                const data = await response.json();
                const course = data.course;
                
                setCourseInfo({
                    title: course.title,
                    description: course.description,
                    price: course.price,
                    // The backend sends trailerLink, thumbnailUrl is not in the schema as a URL
                    trailerLink: course.trailerLink || '',   
                    thumbnailUrl: '', // This field remains unused as per current backend schema
                });

                // Logic to separate final quiz from other subtopics
                const finalQuiz = course.subtopics.find(sub => sub.title === 'Final Quiz');
                const regularSubtopics = course.subtopics.filter(sub => sub.title !== 'Final Quiz');

                if (finalQuiz && finalQuiz.exams && finalQuiz.exams.length > 0) {
                    setFinalQuizExamLink(finalQuiz.exams[0].link || '');
                }

                setSubtopics(regularSubtopics.length > 0 ? regularSubtopics : [emptySubtopic()]);

            } catch (error) {
                console.error("Fetch error:", error);
                alert(error.message);
                navigate('/teacher-dashboard');
            } finally {
                setLoading(false);
            }
        };
        fetchCourseData();
    }, [courseId, navigate]);

    const handleCourseChange = e => {
        setCourseInfo({ ...courseInfo, [e.target.name]: e.target.value });
    };

    const handleSubtopicChange = (idx, field, value) => {
        const updated = [...subtopics];
        updated[idx][field] = value;
        setSubtopics(updated);
    };

    const handleAddSubtopic = () => setSubtopics([...subtopics, emptySubtopic()]);
    const handleRemoveSubtopic = idx => setSubtopics(subtopics.filter((_, i) => i !== idx));

    const handleNestedChange = (type, sIdx, idx, field, value) => {
        const updated = [...subtopics];
        updated[sIdx][type][idx][field] = value;
        setSubtopics(updated);
    };

    const handleAddNested = (type, sIdx) => {
        const updated = [...subtopics];
        updated[sIdx][type].push(type === 'videos' ? { title: '', link: '' } : { link: '' });
        setSubtopics(updated);
    };
    
    const handleRemoveNested = (type, sIdx, idx) => {
        const updated = [...subtopics];
        updated[sIdx][type] = updated[sIdx][type].filter((_, i) => i !== idx);
        setSubtopics(updated);
    };

    const getFinalQuizSubtopic = () => ({
        title: 'Final Quiz',
        videos: [],
        assignments: [],
        exams: [{ link: finalQuizExamLink }],
    });

    const handleSubmit = async e => {
        e.preventDefault();
        
        const requestBody = { 
            ...courseInfo, 
            subtopics: [...subtopics, getFinalQuizSubtopic()]
        };
        const token = localStorage.getItem('token');
        
        try {
            const response = await fetch(`http://localhost:5001/api/teacher/courses/${courseId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify(requestBody),
            });
            
            if (response.ok) {
                alert('Course updated successfully!');
                navigate('/teacher-dashboard');
            } else {
                const data = await response.json();
                throw new Error(data.error || 'Failed to update course.');
            }
        } catch (err) {
            alert('Error: ' + err.message);
        }
    };

    if (loading) return <div className="text-center p-8">Loading...</div>;

    return (
        <div className="container mx-auto px-4 py-12 max-w-3xl">
            <div className="bg-gradient-to-r from-purple-100 to-white rounded-2xl shadow-xl p-8">
                <h1 className="text-4xl font-extrabold mb-8 text-purple-700 text-center">Edit Course</h1>
                <form onSubmit={handleSubmit} className="space-y-10">
                    <div className="bg-white/90 p-6 rounded-xl shadow-md space-y-4">
                         <h2 className="text-2xl font-bold text-purple-700">Course Details</h2>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <input name="title" value={courseInfo.title} onChange={handleCourseChange} placeholder="Course Title" required className="w-full p-3 border rounded-lg" />
                            <input name="price" value={courseInfo.price} onChange={handleCourseChange} placeholder="Price (USD)" type="number" min="0" required className="w-full p-3 border rounded-lg" />
                        </div>
                        <div className="grid grid-cols-1">
                           <div className="relative">
                                <Video className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-gray-400" />
                                <input name="trailerLink" value={courseInfo.trailerLink} onChange={handleCourseChange} placeholder="Trailer Video URL (e.g., YouTube embed link)" className="w-full p-3 pl-10 border rounded-lg" />
                           </div>
                        </div>
                        <textarea name="description" value={courseInfo.description} onChange={handleCourseChange} placeholder="Course Description" rows={4} required className="w-full p-3 border rounded-lg" />
                    </div>

                    {/* Subtopics Section */}
                    <div className="space-y-8">
                        <div className="flex items-center justify-between">
                            <h2 className="text-2xl font-bold text-purple-700">Subsections</h2>
                            <button type="button" onClick={handleAddSubtopic} className="flex items-center gap-2 px-4 py-2 bg-purple-600 text-white rounded-full hover:bg-purple-700 font-semibold shadow">
                                <Plus size={16} /> Add Subsection
                            </button>
                        </div>
                        {subtopics.map((sub, sIdx) => (
                            <div key={sIdx} className="bg-white/90 p-6 rounded-xl shadow-md space-y-4 relative">
                                <button type="button" onClick={() => handleRemoveSubtopic(sIdx)} className="absolute -top-3 -right-3 bg-red-500 text-white p-2 rounded-full hover:bg-red-600 shadow-lg">
                                    <Trash2 size={16} />
                                </button>
                                {/* ... (rest of subtopic mapping) ... */}
                            </div>
                        ))}
                    </div>

                    <div className="text-right mt-8">
                        <button type="submit" className="bg-purple-600 text-white font-bold px-10 py-3 rounded-full shadow-lg">Save Changes</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default EditCoursePage;
