import React from 'react';

// You can replace these with actual image URLs
const teamMemberImg1 = 'https://via.placeholder.com/150';
const teamMemberImg2 = 'https://via.placeholder.com/150';
const teamMemberImg3 = 'https://via.placeholder.com/150';

const AboutUsPage = () => {
  // Styles are defined as JavaScript objects for easy customization.
  const styles = {
    container: {
      fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif',
      color: '#333',
      padding: '2rem',
      maxWidth: '1200px',
      margin: '0 auto',
      lineHeight: '1.6',
    },
    header: {
      textAlign: 'center',
      marginBottom: '3rem',
    },
    h1: {
      fontSize: '3rem',
      color: '#2c3e50',
      marginBottom: '0.5rem',
    },
    tagline: {
      fontSize: '1.2rem',
      color: '#7f8c8d',
    },
    section: {
      marginBottom: '3rem',
    },
    h2: {
      fontSize: '2.2rem',
      color: '#34495e',
      borderBottom: '3px solid #3498db',
      paddingBottom: '0.5rem',
      marginBottom: '1.5rem',
    },
    content: {
      fontSize: '1.1rem',
      color: '#555',
    },
    teamContainer: {
      display: 'flex',
      justifyContent: 'space-around',
      flexWrap: 'wrap',
      gap: '2rem',
      textAlign: 'center',
    },
    teamMember: {
      flexBasis: '300px',
      backgroundColor: '#f9f9f9',
      padding: '1.5rem',
      borderRadius: '10px',
      boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
      transition: 'transform 0.3s ease',
    },
    teamImg: {
      width: '120px',
      height: '120px',
      borderRadius: '50%',
      objectFit: 'cover',
      marginBottom: '1rem',
    },
    h3: {
      fontSize: '1.5rem',
      color: '#2c3e50',
      margin: '0.5rem 0',
    },
    role: {
      fontSize: '1rem',
      color: '#3498db',
      fontStyle: 'italic',
    },
  };

  // To make the team cards lift on hover (optional)
  const handleMouseOver = (e) => {
    e.currentTarget.style.transform = 'translateY(-5px)';
  };
  const handleMouseOut = (e) => {
    e.currentTarget.style.transform = 'translateY(0)';
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1 style={styles.h1}>About Us</h1>
        <p style={styles.tagline}>
          Empowering the next generation of learners through technology. 🚀
        </p>
      </header>

      <hr />

      {/* Our Story Section */}
      <section style={styles.section}>
        <h2 style={styles.h2}>Our Story</h2>
        <p style={styles.content}>
          Founded in 2024, <strong>[Your Website Name]</strong> was born from a simple yet powerful idea: education should be accessible, engaging, and tailored to the modern world. We saw a gap between traditional learning methods and the limitless potential of technology. Our founders, a mix of passionate educators and tech innovators, came together to create a platform that not only delivers high-quality content but also fosters a vibrant community of lifelong learners.
        </p>
      </section>

      <hr />

      {/* Our Mission & Vision Section */}
      <section style={styles.section}>
        <h2 style={styles.h2}>Our Mission & Vision</h2>
        <p style={styles.content}>
          <strong>Our Mission:</strong> To democratize education by providing affordable and world-class learning experiences to anyone, anywhere. We are committed to breaking down barriers to education and equipping our students with the skills they need to succeed in their careers and personal lives.
          <br /><br />
          <strong>Our Vision:</strong> To become the world's leading online learning platform, renowned for our innovative teaching methods, expert instructors, and supportive community. We envision a future where learning is a continuous, enriching part of life.
        </p>
      </section>

      <hr />

      {/* Meet the Team Section */}
      <section style={styles.section}>
        <h2 style={styles.h2}>Meet the Team</h2>
        <div style={styles.teamContainer}>
          <div style={styles.teamMember} onMouseOver={handleMouseOver} onMouseOut={handleMouseOut}>
            <img src={teamMemberImg1} alt="Team Member 1" style={styles.teamImg} />
            <h3 style={styles.h3}>Alex Johnson</h3>
            <p style={styles.role}>Co-Founder & CEO</p>
            <p>A visionary leader with a passion for educational reform and technology.</p>
          </div>
          <div style={styles.teamMember} onMouseOver={handleMouseOver} onMouseOut={handleMouseOut}>
            <img src={teamMemberImg2} alt="Team Member 2" style={styles.teamImg} />
            <h3 style={styles.h3}>Maria Garcia</h3>
            <p style={styles.role}>Head of Curriculum</p>
            <p>An experienced educator dedicated to creating engaging and effective learning content.</p>
          </div>
          <div style={styles.teamMember} onMouseOver={handleMouseOver} onMouseOut={handleMouseOut}>
            <img src={teamMemberImg3} alt="Team Member 3" style={styles.teamImg} />
            <h3 style={styles.h3}>Sam Lee</h3>
            <p style={styles.role}>Lead Developer</p>
            <p>The tech wizard who brings our innovative learning platform to life.</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default AboutUsPage;
