import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { Star, ArrowRight, Loader } from 'lucide-react';

// --- Reusable Course Card Component (Copied from AllCoursesPage for consistency) ---
const CourseCard = ({ course }) => {
    const navigate = useNavigate();

    // Navigate to the course details page on click
    const handleNavigate = () => {
        navigate(`/course/${course.Course_ID}`);
    };

    return (
        <div 
            onClick={handleNavigate}
            className="bg-white rounded-xl shadow-lg border border-gray-200/80 overflow-hidden group cursor-pointer transform hover:-translate-y-2 transition-all duration-300 h-full flex flex-col"
        >
            <div className="relative h-40 bg-gray-200">
                 {course.thumbnail_base64 ? (
                    <img 
                        src={`data:image/png;base64,${course.thumbnail_base64}`} 
                        alt={`${course.Title} thumbnail`}
                        className="w-full h-full object-cover"
                    />
                 ) : (
                    <div className="absolute inset-0 bg-gradient-to-br from-purple-500 to-indigo-600 flex items-center justify-center p-4">
                        <h3 className="text-white text-xl font-bold text-center">{course.Title}</h3>
                    </div>
                 )}
            </div>
            <div className="p-5 flex-grow flex flex-col">
                {/* Use the instructor field from the API */}
                <p className="text-sm text-gray-500 mb-2">By {course.instructor || 'LearnIT Staff'}</p>
                <h4 className="text-lg font-bold text-gray-800 group-hover:text-purple-600 transition-colors duration-300 flex-grow">{course.Title}</h4>
                <div className="mt-auto pt-4 border-t border-gray-100 flex justify-between items-center">
                    <p className="text-xl font-bold text-gray-900">{course.Price > 0 ? `$${course.Price}`: 'Free'}</p>
                    <span className="text-purple-600 group-hover:text-purple-700 font-semibold text-sm flex items-center">
                        View Details <ArrowRight className="h-4 w-4 ml-1 transition-transform duration-300 group-hover:translate-x-1" />
                    </span>
                </div>
            </div>
        </div>
    );
};


// --- Main Search Page Component ---
const SearchPage = () => {
  const [searchParams] = useSearchParams();
  const searchTerm = searchParams.get('query') || '';

  // State for courses, loading, and errors
  const [allCourses, setAllCourses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // **FIX:** Fetch all courses when the component mounts
  useEffect(() => {
    const fetchCourses = async () => {
      setIsLoading(true);
      setError(null);
      try {
        const response = await fetch('http://localhost:5001/api/v1/courses');
        if (!response.ok) {
          throw new Error('Failed to fetch courses for search.');
        }
        const data = await response.json();
        setAllCourses(data.data.courses);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchCourses();
  }, []); // Empty dependency array means this runs once on mount

  // Filter courses based on the search term from the URL
  const filteredCourses = allCourses.filter(course =>
    (course.Title || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
    (course.Description || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
    (course.instructor || '').toLowerCase().includes(searchTerm.toLowerCase())
  );

  const hasResults = filteredCourses.length > 0;

  return (
    <div className="bg-gray-50 min-h-screen">
      <div className="container mx-auto px-4 py-12">
        <div className="text-center mb-12">
          <h1 className="text-4xl md:text-5xl font-extrabold text-gray-900">
            Search Results
          </h1>
          {searchTerm && (
             <p className="text-lg text-gray-600 mt-3">
                Showing results for: <span className="font-semibold text-purple-600">"{searchTerm}"</span>
             </p>
          )}
        </div>
        
        {isLoading ? (
            <div className="flex justify-center items-center py-20">
                <Loader className="h-8 w-8 text-purple-600 animate-spin" />
                <span className="ml-4 text-lg text-gray-600">Searching...</span>
            </div>
        ) : error ? (
            <div className="text-center py-20 text-red-500">
                <p>Error: {error}</p>
            </div>
        ) : hasResults ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
            {filteredCourses.map(course => (
              <CourseCard key={course.Course_ID} course={course} />
            ))}
          </div>
        ) : (
          <div className="text-center py-20">
            <p className="text-gray-600 text-lg">No courses found matching your search.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchPage;
