import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import HomePage from './pages/HomePage';
import CourseDetailPage from './pages/CourseDetailPage';
import LoginPage from './pages/LoginPage';
import SignupPage from './pages/SignupPage';
import CartPage from './pages/CartPage';
import TeachPage from './pages/TeachPage';
import AllCoursesPage from './pages/AllCoursesPage'; // Import the new page
import ProfilePage from './pages/ProfilePage'; // Import the new page
import SearchPage from './pages/SearchPage';

// Main App Component
export default function App() {
  const [currentPage, setCurrentPage] = useState('home'); // 'home', 'courseDetail', 'login', 'signup', 'cart', 'teach'
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [courses, setCourses] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [user, setUser] = useState(null);

    const [cartItems, setCartItems] = useState([]);


  // Set the document title
  useEffect(() => {
    document.title = 'LearnIT - Enc';
  }, []);

  // Check for logged-in user on initial load
  useEffect(() => {
    try {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      if (token && userData) {
        // Here you might want to verify the token with the backend
        // For now, we'll just trust the localStorage data
        setUser(JSON.parse(userData));
      }
    } catch (error) {
      console.error("Failed to parse user data from localStorage", error);
      // Clear corrupted data
      localStorage.removeItem('user');
      localStorage.removeItem('token');
    }
  }, []);
  useEffect(() => {
    const fetchCourses = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get('http://localhost:5001/api/v1/courses');
        // Transform backend data to match frontend component props
        const transformedCourses = response.data.data.courses.map(course => ({
          id: course.Course_ID,
          title: course.Title,
          // NOTE: The following fields are placeholders. You'll need to update your API to provide this data.
          instructor: course.instructor_name, // Now using real data from the backend!
          rating: (Math.random() * (5 - 4.2) + 4.2), // Placeholder - Random rating between 4.2 and 5.0
          reviews: Math.floor(Math.random() * 200000), // Placeholder
          price: course.Price,
          originalPrice: course.Price > 0 ? course.Price * 1.5 : 99.99, // Placeholder
          imageUrl: `https://placehold.co/300x170/E2E8F0/4A5568?text=${encodeURIComponent(course.Title.substring(0, 15))}`,
          bestseller: Math.random() > 0.5, // Placeholder
          shortDescription: course.Description,
          // Placeholder reviews. You would fetch these from your backend.
          reviewsList: [
              { id: 1, user: 'Student A', rating: 5, comment: 'Absolutely fantastic course! Covered everything I needed and more.' },
              { id: 2, user: 'Student B', rating: 4, comment: 'Very comprehensive. A bit fast-paced at times, but the content is top-notch.' },
          ]
        }));
        setCourses(transformedCourses);
      } catch (error) {
        console.error('Error fetching courses:', error);
      } finally {
        setIsLoading(false);
      }
    };
    fetchCourses();
  }, []);

  const handleLogin = (userData, token) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userData));
    setUser(userData);
    setCurrentPage('home');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    // Also clear the cart on logout for this example
    setCartItems([]);
    setCurrentPage('home'); // Navigate to home on logout
  };

  const navigateToPage = (page, data = null) => {
    setSelectedCourse(data);
    setCurrentPage(page);
    window.scrollTo(0, 0); 
    if (page === 'search' && typeof data === 'string') { // Ensure data is a string for search term
      setSearchTerm(data); 
    } // Store the search term
  };

  // Adds a course to the cart if it's not already there.
  const handleAddToCart = (courseToAdd) => {
    setCartItems(prevItems => {
      const isAlreadyInCart = prevItems.some(item => item.id === courseToAdd.id);
      if (isAlreadyInCart) {
        // If the item is already in the cart, do nothing.
        // The UI on the detail page will prevent this from being called again.
        return prevItems;
      }
      // Item not in cart, add it with quantity 1
      return [...prevItems, { ...courseToAdd, quantity: 1 }];
    });
  };

  // New function to remove items from cart
  const handleRemoveFromCart = (courseIdToRemove) => {
    setCartItems(prevItems =>
      prevItems.filter(item => item.id !== courseIdToRemove)
    );
  };

  let content;
  switch (currentPage) {
    case 'home':
      content = <HomePage courses={courses} isLoading={isLoading} setCurrentPage={navigateToPage} />;
      break;
    case 'courseDetail':
      // Pass cartItems to CourseDetailPage to check if the course is already in the cart
      content = <CourseDetailPage course={selectedCourse} setCurrentPage={navigateToPage} addToCart={handleAddToCart} cartItems={cartItems} />;
      break;
    case 'login':
      content = <LoginPage setCurrentPage={navigateToPage} handleLogin={handleLogin} />;
      break;
    case 'signup':
      content = <SignupPage setCurrentPage={navigateToPage} handleLogin={handleLogin} />;
      break;
    case 'cart':
      // Pass cartItems and handleRemoveFromCart to CartPage
      content = <CartPage cartItems={cartItems} setCurrentPage={navigateToPage} removeFromCart={handleRemoveFromCart} />;
      break;
    case 'teach':
      content = <TeachPage />;
      break;
    case 'search':
      content = <SearchPage searchTerm={searchTerm} courses={courses} setCurrentPage={navigateToPage} />;
      break;
    case 'allCourses': // New case for All Courses page
      content = <AllCoursesPage courses={courses} isLoading={isLoading} setCurrentPage={navigateToPage} />;
      break;
    case 'profile': // New case for Profile page
      content = <ProfilePage user={user} />;
      break;
    default:
      content = <HomePage courses={courses} isLoading={isLoading} setCurrentPage={navigateToPage} />;
  }

  const showLayout = currentPage !== 'login' && currentPage !== 'signup';

  return (
    <div className="font-sans antialiased text-gray-900 bg-gray-50 min-h-screen flex flex-col">
      {showLayout && <Navbar setCurrentPage={navigateToPage} user={user} handleLogout={handleLogout} />}
      <main className="flex-grow">
        {content}
      </main>
      {showLayout && <Footer />}
    </div>
  );
}
