import React from 'react';
import { BookOpen, Facebook, Twitter, Instagram } from 'lucide-react';

// Footer Component
const Footer = ({ setCurrentPage }) => {
  const aboutLinks = [
    { name: 'Contact Us', page: 'contact' },
    { name: 'About Us', page: 'about' },   // Assuming an 'about' page will be created
  ];

  const socialMediaLinks = [
    { name: 'Facebook', icon: Facebook, url: 'https://www.facebook.com/yourplatform' },
    { name: 'Twitter', icon: Twitter, url: 'https://twitter.com/yourplatform' },
    { name: 'Instagram', icon: Instagram, url: 'https://instagram.com/yourplatform' },
  ];

  return (
    <footer className="bg-gray-900 text-gray-400 border-t border-gray-700">
      <div className="container mx-auto px-4 py-10">
        <div className="flex flex-col md:flex-row justify-between items-start gap-8 mb-8">
          <div className="flex flex-col sm:flex-row gap-8 md:gap-16">
            {/* About Section */}
            <div>
              <h5 className="font-semibold text-white mb-4 tracking-wide">About</h5>
              <ul className="space-y-2">
                {aboutLinks.map((link, index) => (
                  <li key={index}>
                    <button
                      onClick={() => setCurrentPage(link.page)}
                      className="text-sm hover:text-white hover:underline transition-colors duration-200"
                    >{link.name}</button>
                  </li>
                ))}
              </ul>
            </div>
          </div>

          {/* Social Media Section */}
          <div>
            <h5 className="font-semibold text-white mb-4 tracking-wide">Follow Us</h5>
            <div className="flex space-x-4">
              {socialMediaLinks.map((social, index) => {
                const IconComponent = social.icon;
                return (
                  <a key={index} href={social.url} target="_blank" rel="noopener noreferrer" className="text-gray-400 hover:text-white transition-transform duration-200 hover:scale-110">
                    <IconComponent className="h-6 w-6" />
                  </a>
                );
              })}
            </div>
          </div>
        </div>
        <div className="flex flex-col sm:flex-row justify-between items-center border-t border-gray-700 pt-6">
          <div className="flex items-center mb-4 md:mb-0">
            <BookOpen className="h-8 w-8 text-purple-600" />
            <span className="ml-3 text-xl font-bold text-white">LearnIT</span>
          </div>
          <p className="text-sm text-gray-500">&copy; {new Date().getFullYear()} LearnIT, Inc. All rights reserved.</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
