import React from 'react';

const ProfilePage = ({ user }) => {
  if (!user) {
    // This case should ideally be handled by routing, but as a fallback:
    return (
      <div className="container mx-auto p-8 text-center">
        <p className="text-lg text-gray-600">You must be logged in to view this page.</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">My Profile</h1>
      <div className="bg-white p-8 rounded-lg shadow-md max-w-2xl mx-auto">
        <div className="space-y-4">
          <div>
            <label className="text-sm font-medium text-gray-500">Full Name</label>
            <p className="text-lg text-gray-800">{user.name}</p>
          </div>
          <div>
            <label className="text-sm font-medium text-gray-500">Email Address</label>
            <p className="text-lg text-gray-800">{user.email}</p>
          </div>
          {user.birthdate && (
            <div>
              <label className="text-sm font-medium text-gray-500">Birth Date</label>
              <p className="text-lg text-gray-800">{new Date(user.birthdate).toLocaleDateString()}</p>
            </div>
          )}
          {user.holding && (
            <div>
              <label className="text-sm font-medium text-gray-500">Holding</label>
              <p className="text-lg text-gray-800">{user.holding}</p>
            </div>
          )}
          {user.postalCode && (
            <div>
              <label className="text-sm font-medium text-gray-500">Postal Code</label>
              <p className="text-lg text-gray-800">{user.postalCode}</p>
            </div>
          )}
          {user.thana && (
            <div>
              <label className="text-sm font-medium text-gray-500">Thana</label>
              <p className="text-lg text-gray-800">{user.thana}</p>
            </div>
          )}
          {user.city && (
            <div>
              <label className="text-sm font-medium text-gray-500">City</label>
              <p className="text-lg text-gray-800">{user.city}</p>
            </div>
          )}
          {user.district && (
            <div>
              <label className="text-sm font-medium text-gray-500">District</label>
              <p className="text-lg text-gray-800">{user.district}</p>
            </div>
          )}
          {user.country && (
            <div>
              <label className="text-sm font-medium text-gray-500">Country</label>
              <p className="text-lg text-gray-800">{user.country}</p>
            </div>
          )}
          {typeof user.isAuthor === 'boolean' && ( // Check for boolean type as it could be null
            <div>
              <label className="text-sm font-medium text-gray-500">Instructor Status</label>
              <p className="text-lg text-gray-800">{user.isAuthor ? 'Yes' : 'No'}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
