import React, { useState, useEffect } from 'react';
import { useParams, Navigate } from 'react-router-dom';
import apiClient from '../../api/apiClient'; 

const EnrolledRoute = ({ children }) => {
    const { courseId } = useParams();
    const [isEnrolled, setIsEnrolled] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const checkEnrollment = async () => {
            try {
                // Use the new apiClient here, no need for token/headers
                const response = await apiClient.get(`/api/enrollments/${courseId}`);
                setIsEnrolled(response.data.enrolled);
            } catch (error) {
                console.error("Enrollment check failed:", error);
                setIsEnrolled(false);
            } finally {
                setIsLoading(false);
            }
        };

        checkEnrollment();
    }, [courseId]);

    if (isLoading) {
        return <div>Loading...</div>; // Or a spinner component
    }

    if (!isEnrolled) {
        // If not enrolled, redirect to the main course detail page or homepage
        return <Navigate to={`/course/${courseId}`} replace />;
    }

    return children;
};

export default EnrolledRoute;
